css:
/* AGENT_SHEET */
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

scrollbar {
-moz-appearance: none !important;
background: none !important;
border: none !important;
border-width: 0px !important;
}

scrollbar[orient="vertical"]
{
-moz-appearance: none !important;
min-width: 15px !important;
max-width: 15px !important;
width: 15px !important;
border: none !important;
background: -moz-linear-gradient(left, #ddd, #fff) !important;
background: none white bottom no-repeat !important;
}

scrollbar[orient="horizontal"]
{
-moz-appearance: none !important; 
background-color: transparent !important;
min-height: 15px !important;
max-height: 15px !important;
height: 15px !important;
border: none !important;
background: -moz-linear-gradient(top, #ddd, #fff) !important;
background: url("https://lh3.googleusercontent.com/-9fXyprOXpnU/T6_M05_U3xI/AAAAAAAAAoY/oJ-pyAn3xa0/I/Untitled.jpg") white right no-repeat !important;
}

scrollbar[orient="vertical"] thumb *,
scrollbar[orient="horizontal"] thumb *
{
-moz-appearance: none !important;
background: none !important;
border: none !important;
}

scrollbar thumb[orient="vertical"]:hover, scrollbar thumb[orient="vertical"]:active
{
min-width: 15px !important;
max-width: 15px !important;
height: 15px !important;
-moz-appearance: none !important;
background: none !important;
border: none !important;
-moz-border-image: url("https://lh3.googleusercontent.com/-9fXyprOXpnU/T6_M05_U3xI/AAAAAAAAAoY/oJ-pyAn3xa0/I/Untitled.jpg")
6 6 6 6 / 6px 6px 6px 6px  !important;
min-height: 45px !important;
}

scrollbar thumb[orient="horizontal"]:hover, scrollbar thumb[orient="horizontal"]:active
{
min-height: 15px !important;
max-height: 15px !important;
height: 15px !important;
-moz-appearance: none !important;
background: none !important;
border: none !important;
-moz-border-image: url("https://lh3.googleusercontent.com/-9fXyprOXpnU/T6_M05_U3xI/AAAAAAAAAoY/oJ-pyAn3xa0/I/Untitled.jpg")
6 6 6 6 / 6px 6px 6px 6px  !important;
min-width: 45px !important;
}

scrollbar thumb[orient="vertical"]
{
width: 15px !important;
min-width: 15px !important;
max-width: 15px !important;
-moz-appearance: none !important;
background: none !important;
border: none !important;
-moz-border-image: url("https://lh3.googleusercontent.com/-9fXyprOXpnU/T6_M05_U3xI/AAAAAAAAAoY/oJ-pyAn3xa0/I/Untitled.jpg")
6 6 6 6 / 6px 6px 6px 6px  !important;
min-height: 45px !important;
}

scrollbar thumb[orient="horizontal"]
{
height: 15px !important;
min-height: 15px !important;
max-height: 15px !important;
-moz-appearance: none !important;
background: none !important;
border: none !important;
-moz-border-image: none
6 6 6 6 / 6px 6px 6px 6px  !important;
min-width: 45px !important;
}

scrollbar scrollbarbutton
{
-moz-appearance: none !important;
background: none !important;
-moz-border-radius: 0px !important;
}

scrollbar[orient="vertical"] scrollbarbutton[type="decrement"]:hover, scrollbar[orient="vertical"] scrollbarbutton[type="decrement"]:active
{
background: url("https://lh3.googleusercontent.com/-9fXyprOXpnU/T6_M05_U3xI/AAAAAAAAAoY/oJ-pyAn3xa0/I/Untitled.jpg")
-30px 0px no-repeat !important;
}

scrollbar[orient="vertical"] scrollbarbutton[type="decrement"]
{
margin: 0px 0px -10px 0px !important;
background: url("https://lh3.googleusercontent.com/-9fXyprOXpnU/T6_M05_U3xI/AAAAAAAAAoY/oJ-pyAn3xa0/I/Untitled.jpg")
-15px 0px no-repeat !important;
width: 15px !important;
height: 27px !important;
border: none !important;
}

scrollbar[orient="vertical"] scrollbarbutton[type="increment"]:hover, scrollbar[orient="vertical"] scrollbarbutton[type="increment"]:active
{
background: url("https://lh3.googleusercontent.com/-9fXyprOXpnU/T6_M05_U3xI/AAAAAAAAAoY/oJ-pyAn3xa0/I/Untitled.jpg")
0px 1px no-repeat !important;
}

scrollbar[orient="vertical"] scrollbarbutton[type="increment"]
{
margin: -10px 0px 0px 0px !important;
background: none
0px 1px no-repeat !important;
width: 15px !important;
height: 27px !important;
border: none !important;
}

scrollbar[orient="horizontal"] scrollbarbutton[type="decrement"]
{
margin: 0px -10px 0px 0px !important;
background: none
-26px 0px no-repeat !important;
border: none !important;
width: 26px !important;
height: 14px !important;
}
scrollbar[orient="horizontal"] scrollbarbutton[type="increment"]
{
margin: 0px 0px 0px -10px !important;
background: none
0px 0px no-repeat !important;
border: none !important;
width: 26px !important;
height: 14px !important;
}

scrollbar[orient="horizontal"] scrollbarbutton[type="decrement"]:hover, scrollbar[orient="horizontal"] scrollbarbutton[type="decrement"]:active
{
background: url("https://lh3.googleusercontent.com/-9fXyprOXpnU/T6_M05_U3xI/AAAAAAAAAoY/oJ-pyAn3xa0/I/Untitled.jpg")
-52px 0px no-repeat !important;
border: none !important;
width: 26px !important;
height: 14px !important;
}

scrollbar[orient="horizontal"] scrollbarbutton[type="increment"]:hover, scrollbar[orient="horizontal"] scrollbarbutton[type="increment"]:active
{
background: none
0px 0px no-repeat !important;
border: none !important;
width: 26px !important;
height: 14px !important;
}
