css:
@-moz-document domain("pinterest.com") {

body{
color: #ddd !important;
background: #0D0D0D url('http://i.imgur.com/5W17o.jpg') 
}

div.pin {
background-color: #212121;
}

.pin .PinImage {
background-color: #212121;
}

div.convo.attribution.clearfix {
background-color: #0d0d0d;
}

div.write.convo.clearfix {
background-color: #3b3b3b;
}

div.comment.convo.clearfix {
background-color: #2b2b2b;
}

a:-webkit-any-link {
color: #445461;
}

.feed .story {
display: block;
color: #FAFAFA;
}

#PinPinner #PinnerName a {
color: #FFC300;
text-decoration: none;
}

.PinComments .comment p {
color: #FAFAFA;
}

.convo {
padding: 0px 0px;
}

.Nag {
display: none !important;
}

div.comments, div.pin .write{
display: none !important;
}

#zoomScroll.visible {
background-color: rgba(2, 2, 2, 0.93);
}

#Header {
height: 50px !important;
opacity: 0.5;
}

#ColumnContainer {
margin-top: 44px !important;
