css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("9gag.com") {

/* Hide Ads & Stuff */
body#page-landing > div#container > div.side-dock > div.s-300,
body#page-landing > div#container > div.side-dock > div#top-gag-stay > div.s-300,
body#page-landing > div#container > div.side-dock > div#top-gag-stay > div#sidebar-box-0,
body#page-landing > div#container > div.side-dock > div#jsid-sidebar-footer,
div.content > div.fatbigdick.all-users-expand,
body > p.green-pea,
body#page-profile > div#container > div.side-dock,
body#page-landing > div#container > div.side-dock > div#special-button,
body#page-landing > div#container > div.side-dock > div.social-block,
div#main-bottom-ad-tray {display:none !important;}

/* Body */
body {min-width:1000px !important;}
body {background:#4b4b4b !important; min-width:50% !important;}

/* Headbar */
div#headbar-wrap {
	transition:all 500ms ease 0ms;
	-moz-transition:all 500ms ease 0ms;
	-webkit-transition:all 500ms ease 0ms;
	-o-transition:all 500ms ease 0ms;
	opacity:.7;
}
div#headbar-wrap:hover {opacity:1;}

/* Content */
img[style="max-width:460px;"], div.img-wrap > a.all-users-link > img[style="max-width:460px; display:block;"], div.content > div[class="video-post all-users-expand"] > object[class="video-element"], div[class="video-post all-users-expand"] > object[class="video-element"] > embed[class="video-element"] {
    max-width:100%!important;
    width:100%!important;
    height:auto!important;
}
div#content, div#block-content > div.content-title {background-color:#e8e8e8 !important;}
div#main {margin-left:auto !important; margin-right:auto !important;}
div#container, div#support-main {width:900px !important; min-width:740px;}
div#block-content > div.profile-pad > div.profile-image {width:55% !important;}
div#block-content > div.profile-pad > div.profile-info {width:40% !important;}
ul#entry-list-ul > li.entry-item > div.content {min-width: 460px; width:600px !important;}
ul#entry-list-ul > li.entry-item > div.content > div.img-wrap, input#interaction_name, input#interaction_email, input#email_subject, textarea#email_body, div#pagination > a#next_button, div#pagination > a#more_button, div#main, div#modal-post, div#block-content > div.profile-pad, div#block-content > div.profile-pad > div.profile-image > a > img {width:100% !important;}
div.static-block > ul.qa-list > li > img, div.static-block > ul.qa-list > li > a > img {width:100% !important; height:auto !important;}
div#entry-comments {margin-right:20px;}
div#entry-comments > .fb_iframe_widget, div#entry-comments > .fb_iframe_widget > span, div#entry-comments > .fb_iframe_widget > span > iframe {width:100% !important; min-width:700px;}
ul#entry-list-ul > li.entry-item > div.content {margin-right:10px !important;}
ul#entry-list-ul > li.entry-item > div[class="info jump_stop jump_focus "], ul#entry-list-ul > li.entry-item > div[class="info jump_stop jump_focus "] > div[class="sticky-items "] {width:230px !important;}
li[class="facebook sharing-row"] > div[class="b2-widget"] {width:100px !important;}

/* Feedback Button */
div.feedback-button {left:-1px !important; top:20% !important; width:20px !important; }
div#container > div.feedback-button > a {border-radius:0px 5px 5px 0px !important;}

/* Post Control Bar */
div#post-control-bar {
    transition:all 500ms ease;
    -moz-transition:all 500ms ease;
    -o-transition:all 500ms ease;
    -webkit-transition:all 500ms ease;
    opacity:0.5;
    width:900px !important;
    min-width:740px;
}
div#post-control-bar:hover {opacity:1;}
div#post-control-bar > div.spread-bar > div.twitter-btn  > div.b2-widget {width:130px !important;}

/* Post Content */
div#content > div.post-container > div.img-wrap > img[style], div#content > div.post-container > div.img-wrap > a > img[style] {max-width:850px !important; width:100% !important; margin: 0 auto 0 !important;}
div#content > div.post-container > div.img-wrap {background-color:#E8E8E8 !important;}
div#content > div.post-container > div.img-wrap > a {width:100% !important;}

/* Post Sidebar */
body#page-post > div#container > div > div#special-button {display:none;}
body#page-post > div#container > div > div.s-300 {display:none;}
body#page-post > div#container > div > div#sidebar-box-0 {display:none;}

/* Post Recommended */
body#page-post > div#container > div > div#post-gag-stay {
    position:fixed;
    top:75px;
    right:-280px;
    border-radius:10px 0 0 10px;
    border:1px solid gray;
    padding:10px;
    background-color:#E8E8E8;
    z-index:10;
    overflow-y:scroll;
    overflow-x:hidden;
    transition:all 500ms ease 0ms;
	-moz-transition:all 500ms ease 0ms;
	-webkit-transition:all 500ms ease 0ms;
	-o-transition:all 500ms ease 0ms;
	height:420px !important;
}
body#page-post > div#container > div > div#post-gag-stay:hover {right:-2px; }
body#page-post > div#footer {width:900px !important;}

/* Post Page (Fast) */
html body#page-post div[style="position:fixed;top:0px;right:0px;z-index:9999;background-color:black;width:392px;height:290px"],
html body#page-post div div#block-content div > div.right-comment > div[style="height:290px;background-color:none;"] {display:none;}

/* Search Page */
body#page-search > div#container > div#main > div#block-content > div > form#searchbar > div > input#page_search.search {width:800px !important;}
body#page-search > div#container > div#main > div#block-content > div#content > div#entry-list > ul > li {width:270px !important;}
body#page-search > div#container > div#main > div#block-content > div#content > div#pagination > a {width:420px !important;}
body#page-search > div#container > div.side-dock {display:none;}
body#page-search > div#footer {width:900px !important;}
body#page-search > div#footer > div.section-2 > div.wrap { margin:0 auto;}

/* Setting Page */
form#form-settings > div[class="field profile-pic"] > div.wrap {width:810px !important; min-width:740px;}
form#form-settings > div.field > label > input[class="text tipped"] {width:630px !important; min-width:478px;}

/* Submit Page */
body#page-post-item > div#container > div.side-dock {
    transition:all 500ms ease 0ms;
	-moz-transition:all 500ms ease 0ms;
	-webkit-transition:all 500ms ease 0ms;
	-o-transition:all 500ms ease 0ms;
    background-color:white;
    border:1px solid gray;
    border-radius:6px;
    padding:10px;
	z-index:13;
	width:300px;
	opacity:0.7;
	position:fixed;
    top:65px;
    right:-280px;
    height:auto;
}
body#page-post-item > div#container > div.side-dock:hover {opacity:1; right:-1px;}
body#page-post-item > div#container > div.side-dock > div[class="message-block notice"] {margin-bottom:10px !important;}
body#page-post-item > div#container > div.side-dock > div.message-block {margin-bottom:0px !important;}
body#page-post-item > div#container > div#footer > div.section-2 {width:900px!important; min-width:740px;}

/* Vote Page */
div#entry-list > ul.col-1 > li.entry-item > div.content > div.img-wrap > a.all-users-link > img[style="max-width: 460px; display: block;"] {max-width:100% !important; width:100% !important;}
div#entry-list > ul.col-1 > li.small-thumb > div.info > div[class="sticky-items  collpased"] {width:220px !important;}
div#entry-list > ul.col-1 > li.small-thumb > div.content > div.video-post > img {display:none !important;}
div#entry-list > ul.col-1 > li.entry-item > div.content > div.img-wrap > a.all-users-link > img {max-width:100% !important; width:100% !important;}

/* About Page */
body#page-about > div#container > div.side-dock {display:none;}

/* FAQ */
body#page-faq > div#container > div.side-dock {display:none;}

/* TOS */
body#page-terms > div#container > div.side-dock {display:none;}

/* Privacy */
body#page-privacy > div#container > div.side-dock {display:none;}

/* Advertise Page */
body#page-advertise > div#container {width:1060px !important;}
body#page-advertise > div#container > div#container {width:100% !important;}

/* Footer */
div#footer {width:900px !important;}
div#footer > div.section-2 > div.wrap { margin:0 auto;}

}
