css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url(http://www.facebook.com/), url(http://de.facebook.com/), url(http://fr.facebook.com/), url(http://es.facebook.com/) {
#content_frame, #pagefooter, #widebar, #label_email, #label_pass, a[href="http://www.facebook.com/reset.php"], #remember_me_text, #persistent, #doquicklogin, #sidebar_ads {
display: none !important
}
#book {
margin-left: 43% !important
}
}
@-moz-document url(https://login.facebook.com/login.php) {
#navigator, #sidebar_signup_content, #pagefooter, .login_title_header, .shorten add_border, #error, #label_email, #label_pass, a[href="http://www.facebook.com/reset.php"], #remember_me_text, #persistent, #doquicklogin, #sidebar_ads, #buttons{
display: none !important
}

#content, #content_shadow {
border: 0 !important
}

#book {
width: 256px !important;
margin-left: 43% !important
}

input {
width: 130px !important; 
margin-left: -140px !important;
background: #ffebe8 !important 
}


}
