css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mint.com") {

  #as-nav-content div.card.closed {
    background: -moz-linear-gradient(top, #D6D6D6, #B1B1B1) !important; /* (top,#CBDADA,#9DBBB1) */
    background: -webkit-gradient(linear, left top, left bottom, from(#D6D6D6), to(#B1B1B1)) !important; /* from(#CBDADA),to(#9DBBB1) */
    border-color: #707070 !important; /* #637670 */
  }

  #as-nav-content div.card.closed div.card_deets {
    border-color: #ACACAC !important; /* 9BB5AD */
    background: -webkit-gradient(linear, left top, left bottom, from(#DFDFDF), to(#CACACA)) !important; /* from(#D7E2E1),to(#BCD1CA) */
    background: -moz-linear-gradient(top, #DFDFDF, #CACACA) !important; /* (top,#D7E2E1,#BCD1CA) */
  }

  #as-nav-content div.card.closed div.card-controls a.button.editlogin,
  #as-nav-content div.card.closed div.card-controls a.button.delete {
    background-color: #D6D6D6 !important; /* #CCDBDA */
    border: 1px solid #B2B2B2 !important; /* #9EBCB2 */
  }

  #as-nav-content div.card.closed div.card-header a.primary.button {
    -webkit-filter: grayscale(100%) !important;
    -moz-filter: grayscale(100%) !important;
  }

  #as-nav-content div.card.closed div.card-CS_Message {
    background-color: #D7D7D7 !important;
  }

}
