css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('youtube.com'), url-prefix('http://plus.googleapis.com'), 
url-prefix("http://apis.google.com"),url-prefix('https://plus.googleapis.com'), 
url-prefix("https://apis.google.com") {

/*Colors*/
body, #watch7-sidebar, #footer-container, #masthead-container,#yt-masthead-container, #playlist-bar-bar-container, #watch7-content, #watch7-user-header, #watch7-action-panel-footer, #watch7-headline {background:url("http://i.imgur.com/lrhPp.png") !important;}
#yt-masthead-container {border-bottom:none !important}
#watch-description-toggle, #watch-description-collapse {border-top:none !important}
#yt-masthead-container {border-style: none !important;}
#masthead-expanded-container {border-style: none !important;}
.yJa,
.uPa.BJa,
.Mza.mj,
.DJa,
.RN.Nza.E5,
.xN,
.r0,
.yDa,
.dga
 {
  background-color: transparent !important;
  background-image: none !important;
  -moz-appearance: none !important;
}

#yt-masthead #logo {
 background-image: url("http://i.imgur.com/1HuEFUo.png") !important;
 width: 102px !important; 
 height: 40px !important; 
 background-position: 0px 0px !important; 
}

/* Buttons */
#watch-like {background: -moz-linear-gradient(center top , chartreuse, #EFEFEF) repeat scroll 0 0 #F6F6F6 !important;}/*like button*/
#watch-dislike {background: -moz-linear-gradient(center top , #D72D2D, #EFEFEF) repeat scroll 0 0 #F6F6F6 !important;}/*dislike button*/
.yt-subscription-button {background: -moz-linear-gradient(center top , #FFF9C1, #FED81C) repeat scroll 0 0 #FFE971 !important;}/*subscribe button*/

/*Fix dislike bar */
.video-extras-sparkbar-dislikes{background:#C22!important}
.video-extras-sparkbar-likes{background:#009900!important}

/* Hides avatars (doesn't affect video author and your own avatar) */
.Uk, .go {display: none !important;} /* Avatars and answer avatars */
.ve.oba.HPa {margin-left: -10px !important;} /* Comments */
.Wi.lg.via {padding-left: 0px !important;} /* Answer comments */

/* Removes the guide on the video page */
#guide-container.collapsible-guide {display: none !important;}

/* Older style username */
.yt-masthead-user-icon
{display: none !important;}

yt-masthead-user #sb-button-notify{background: none !important;}
#yt-masthead-user #sb-button-notify:before
{
    position: relative !important;
    margin-left: 18px !important;
    font-size: 11px !important;
    font: arial !important;
    color: #666666 !important;
    margin-top: 7px !important;
    margin-left: -16935% !important;
}

#yt-masthead-user .yt-uix-button-content {margin-left: -980px !important;}
#yt-masthead-user #sb-button-notify .yt-uix-button-icon-bell{display: none !important;}
#ad_creative_iframe_1{display: none !important;}
#yt-masthead-user-displayname
{
    position: relative !important;
    margin-left: 18px !important;
    font-size: 11px !important;
    font: arial !important;
    color: #666666 !important;
    margin-top: 1px !important;
    margin-right: -33px !important;
}

#sb-container
{
    margin-left: 18px !important;
    font-size: 11px !important;
    font: arial !important;
    color: #666666 !important;
}

DIV#masthead-expanded-menu-google-column1, #masthead-expanded-menu-account-container, #masthead-expanded-google-menu .masthead-expanded-menu-header{display: none !important;}
#masthead-expanded-menu-google-column2
{

    margin-left: -165px !important;
    margin-top: 55px !important;
}

#masthead-expanded-menu-list {margin-top: -10px !important;}
#masthead-expanded-menu .masthead-expanded-menu-header{display: none !important;}
#yt-masthead-user #sb-button-notify {background: none !important;}
#yt-masthead-user #sb-button-notify:before {content: 'Inbox' !important;}
#UCs5XULb_2tok6gN7_JjNWZg-guide-item .display-name
    {
    font-family: arial !important;
    color: #FF0000 !important;
    font-weight: bold !important;
    }
    
/* Description buttons changed to old style */
#watch-description-toggle button {
background-color: transparent !important;}
#watch7-action-panels #watch7-action-panel-footer
{display: none !important;}

/* Description shadow (old style) */
#watch-description-expand.expand {
border-style: solid none none !important;
border-width: 1px !important;
border-color: #ffffff !important;
box-shadow: 0px 0px 60px #888888 !important;}

/* Makes like buttons more visible */
#watch-like, #watch-dislike {background-color: #f1f1f1 !important; border-color: #cccccc !important;}

/* Random fixes */
.bmd {color: #009900 !important; margin-right: 2px !important;}
#watch7-headline span {color: #333333 !important; font-size: 20px !important;} 
#watch7-headline a {color: #438bc5 !important;}
.Caa {display: none !important;}

#alerts, 
#initial-feed-promo, 
#my-subscriptions-promo, 
#channel-subscription-promo-in-feed, 
#watch7-branded-banner, 
.iph-dialog, .iph-bracket-left, 
.guide-section.guide-header.signup-promo, 
.watch7-card-promo {display: none !important;}

}
