@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("https://userstyles.org/"), url-prefix("https://kinda-static.userstyles.org/") {
/* url-prefix("http://userstyles.org.nyud.net/") */

/*all caps, "professional"*/ article[author-id="95941"],
/*spammer, Geliyoo, arama motoru*/ article[author-id="45144"],
/*facebook multiple, friends with benefits*/ article[author-id="82128"],
/*facebook multiple, templah.com*/ article[author-id="8179"],
/*facebook multiple, facebookskin.com*/ article[author-id="27517"],
/*facebook multiple, FBJason*/ article[author-id="35500"],
/*facebook multiple, "cool layout"*/ article[author-id="55301"],
/*facebook abstract magik*/ article[author-id="24136"],
/*uso black shiny*/ article[author-id="23263"],
/*uso enlarge screenshots on hover*/ article[author-id="3168"],
/*bad namespace, single trash style*/ article[author-id="13448"],
/*trash code*/ article[author-id="24140"], article[author-id="23679"],

.ad, #social, #post-install-ad, #donate { display: none !important; }

html { background-color: #ccc /* #ddb #eed #fed #def #b74 */; }

body { width: 98% !important; min-width: 750px !important; max-width: 1000px !important; margin: 0 auto 50px !important; padding: 0 0 20px !important; border-radius: 0 0 23px 23px !important; box-shadow: 0 0 16px #000; background: #fff; }

/*spacing links on homepage*/ ul li { padding: 1px 0 !important; }
/*removing borders and outlines*/
a { outline: none !important; text-decoration: none !important; }
.install-status a { text-decoration: underline !important; }
::-moz-focusring { outline: none !important; }
::-moz-focus-inner { border-color: transparent !important; }
.obsolete > a { opacity: 0.6 !important; }
/*my account, hovered style*/ .author-styles > tbody > tr:hover { outline: 1px solid #555; }
/*my account, style table*/ .author-styles { margin: 0 20px 0 -150px !important; }
/*my account, cell height*/ .author-styles > tbody > tr > td { height: 30px !important; vertical-align: middle !important; }
.form-controls > tbody > tr > th { display: block !important; margin-top: 1em !important; text-align: left !important; border-top: 1px solid; border-radius: 5px; }
.form-controls > tbody > tr > td { display: block !important; max-width: 800px; }

#main-header { position: relative !important; margin: 0 !important; padding: 45px 20px 5px !important; border-style: solid !important; border-width: 0 0 1px !important; border-color: #666; }
#main-header > h1, #obsoletion-message { text-align: center !important; }
#main-header > h1:before { content: "S"; position: absolute; width: 1.2em; height: 1.2em; margin: -1px 0 0 -0.8em; border: 1px solid; border-radius: 2px 7px; font: bold 1em/1.2 "Trebuchet MS"; color: #fff; background: #f96; }
#main-header > h1 > a { font-size: 0 !important; position: absolute; left: 5px; bottom: 13px; }
#main-header > h1 > a:before { content: ''; display: block; border-width: 0 16px 16px 16px; border-style: solid; border-color: transparent transparent #09f transparent; }
#main-header > h1 > a:after { content: ''; border: 8px solid #09f; }

#content-wrapper { margin: 0 !important; min-height: 640px !important; }
article { padding: 10px 12px 10px 0 !important; }
article > section { padding: 5px 0 !important; }
/**/#style-info { min-height: 18em !important; }/**/
#additional-info-text { max-height: 50em !important; }
article > section > h2 { margin: 10px 0 !important; }
article > section > p { padding: 0 10px !important; }
article > div > header { font-size: 1.5em !important; }
#main-article > .pagination { font-size: 14px !important; }
article.good-rating { background: rgba(110,230,110,0.3); }
article.ok-rating { background: rgba(230,230,110,0.3); }
article.bad-rating { background: rgba(230,110,110,0.3); }
#main-article > article { padding-left: 9px !important; border-radius: 7px !important; }

#advanced-search { display: block !important; }
#search-submit, #show-advanced-search { display: none !important; }

#screenshots { background: none !important; border-style: none !important; font-size: 11px !important; border-radius: 0 !important; }
#screenshots > figure { margin: 0 0 10px !important; padding: 0 !important; text-align: center !important; }
#screenshots img { max-width: 330px !important; max-height: 200px !important; margin: 0 !important; padding: 6px !important; border-radius: 4px !important; }
div[id="screenshots"] img { background: #ddd !important; }
#more-screenshots span { display: inline-block !important; margin-bottom: 3px !important; }

#info-tables > table > tbody > tr > th { font-weight: normal; }
#style-author-info { border-top: 1px solid; border-top-left-radius: 5px; }
#style-install-info { border-top: 1px solid; border-top-right-radius: 5px; }

#post-discussion { width: 970px !important; margin-left: -180px !important; }

#show-button { position: absolute !important; top: 7px !important; right: 50% !important; z-index: 2 !important; display: block !important; width: 100px !important; height: 20px !important; margin: 0 250px 0 0 !important; padding: 5px 0 !important; font-size: 14px !important; text-align: center !important; text-shadow: none !important; background: #fff; border: 1px solid #444; border-radius: 5px; }

form[action="/styles/create"]:before, form[action="/styles/update"]:before { content: ''; position: fixed; top: 0; margin: 0 0 0 -30px; width: 700px; height: 26px; background: #999; border-width: 0 1px 1px 1px; border-style: solid; border-color: inherit; border-radius: 0 0 3px 3px; }
#main-article > form > input[value="Save"] { position: fixed !important; top: 0 !important; }
#style_short_description { position: fixed !important; top: 0 !important; margin: 2px 0 0 130px !important; padding: 2px; border-width: 1px; }
label[for="short-description"] { position: fixed !important; top: 0 !important; margin: 4px 0 0 80px !important; font-size: 13px; }
label[for="short-description"]+br { display: none !important; }

#view-code { position: fixed !important; top: 45px; right: 0; bottom: 0; left: 0; max-height: none !important; max-width: 960px !important; overflow: auto !important; margin: auto !important; padding: 20px !important; border-width: 9px !important; border-style: solid !important; background: #fff; outline: 450px solid rgba(0,0,0,0.5) !important; }
#stylish-code { font-size: 13px !important; font-family: Consolas, "DejaVu Sans Mono", monospace !important; }

#style-info .install-status { position: absolute !important; top: 5px !important; left: 0 !important; right: 0 !important; margin: 0 auto !important; padding: 5px 15px !important; text-align: center !important; max-height: 1.5em !important; max-width: 430px !important; overflow: hidden !important; }
#style-info .install-status:hover { max-height: none !important; }
.install-status > p { margin: 0 0 0.5em !important; }

.license-title:before, .license-image:before { position: absolute; top: 12px; left: 50%; width: 80px; height: 20px; margin-left: 250px; border-radius: 5px; text-align: center; vertical-align: middle; font: 14px/20px monospace; color: #fff; }
.license-image[href*="/publicdomain/zero/"]:before { content: "zero"; background: #292; }
.license-image[href*="/by/"]:before { content: "by"; background: #28c; }
.license-image[href*="/by-sa/"]:before { content: "by-sa"; background: #772; }
.license-image[href*="/by-nc/"]:before { content: "by-nc"; background: #555; }
.license-image[href*="/by-nd/"]:before { content: "by-nd"; background: #922; }
.license-image[href*="/by-nc-sa/"]:before { content: "by-nc-sa"; background: #555; }
.license-image[href*="/by-nc-nd/"]:before { content: "by-nc-nd"; background: #922; }
.license-title:before { content: "\a9"; font-size: 20px; background: #922; }

.pagination > .current { padding: 0 5px; border: 1px solid; border-radius: 5px; }
.pagination > a { padding: 0 5px; border: 1px solid transparent !important; border-radius: 5px; }
.pagination > a:hover { border-color: currentColor !important; }

/* Opera fix */ html > body > pre:only-child { white-space: pre-wrap !important; }

.good-average-rating, .ok-average-rating, .bad-average-rating { width: 0 !important; height: 10px !important; padding-left: 15px !important; background-position: center !important; background-repeat: no-repeat !important; }

.good-average-rating { background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAKCAMAAAC67D+PAAAADFBMVEX///8AuwAAuwAAuwATNobKAAAAA3RSTlMAQMCRa0lrAAAAKUlEQVR42j3IsQ0AMBCDwDfef+fIBaFCdys5Ay+tTMtHmS3iWBwPZVweFiEAl67oiTcAAAAASUVORK5CYII=") !important; }
.good-rating { list-style-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAKCAMAAAC67D+PAAAADFBMVEX///8AuwAAuwAAuwATNobKAAAAA3RSTlMAQMCRa0lrAAAAKUlEQVR42j3IsQ0AMBCDwDfef+fIBaFCdys5Ay+tTMtHmS3iWBwPZVweFiEAl67oiTcAAAAASUVORK5CYII=") !important; }

.ok-average-rating { background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAgAAAAICAMAAADz0U65AAAAElBMVEX/////lCn/kSv/kSr/kSv/kSqaNIaLAAAABXRSTlMAH6fm8KO/rOsAAAAoSURBVHjaPYtJCgAACAJd6v9fLgqciwMqQNniZvVShPoQ/OJIqoxzHx/IAOHqjwKhAAAAAElFTkSuQmCC") !important; }
.ok-rating { list-style-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAgAAAAICAMAAADz0U65AAAAElBMVEX/////lCn/kSv/kSr/kSv/kSqaNIaLAAAABXRSTlMAH6fm8KO/rOsAAAAoSURBVHjaPYtJCgAACAJd6v9fLgqciwMqQNniZvVShPoQ/OJIqoxzHx/IAOHqjwKhAAAAAElFTkSuQmCC") !important; }

.bad-average-rating { background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAKCAMAAAC67D+PAAAADFBMVEX/////AAD/AAD/AADrn5EnAAAAA3RSTlMAQMCRa0lrAAAAKklEQVQI1z3IsRGAABDDMCXsvzMFH1z5pM9V2YYOjcNxGYfjD8mPdEgCLysvAJdvR5KXAAAAAElFTkSuQmCC") !important; }
.bad-rating { list-style-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAKCAMAAAC67D+PAAAADFBMVEX/////AAD/AAD/AADrn5EnAAAAA3RSTlMAQMCRa0lrAAAAKklEQVQI1z3IsRGAABDDMCXsvzMFH1z5pM9V2YYOjcNxGYfjD8mPdEgCLysvAJdvR5KXAAAAAElFTkSuQmCC") !important; } 

.no-rating { list-style-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAgAAAAICAMAAADz0U65AAAAD1BMVEX///+Li4uPj4+Ojo6Pj49Lfd8bAAAABXRSTlMAFkRISxkkdvsAAAAnSURBVHjaPYtBDgAADMEw/3/zLEu4tAkAWjLDQTKEcQn0okqrjntfCdgARVqRYrEAAAAASUVORK5CYII=") !important; }

}
