css:
@namespace url(http://www.w3.org/1999/xhtml); @-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/') 
 {

body {background: url('http://oi49.tinypic.com/2wg65vl.jpg ') top left #f5eedc no-repeat fixed !important;}
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:#f5eedc !important; background: rgba(191,170,141,.6) !important;}
*{text-shadow:none !important; color:#6d523a !important;}
ol#posts li.notification{background-color: #4A3F37 !important; border-bottom: 2px solid #6D523A !important; color: #bfaa8d !important;opacity: .90;}
#right_column a#dashboard_switch_blog_menu_current:hover {background:#bfaa8d;opacity: .90;}
#nav .nav_item.active{background-color:#bfaa8d }
#left_column #posts .post .permalink{background-color:#bfaa8d;opacity: .110;}
#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current
{background-image:none !important}
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) { 
	background: none !important;
	width: 0px !important;
	height: 0px !important;
	margin-top: 0px !important;
	border-bottom: solid 11px #51443B !important;
	border-right: 11px solid transparent !important;
	border-left: 11px solid transparent !important; }
.selection_nipple:not([class="selection_nipple white"]) {
	border-bottom: solid 11px #51443B !important; opacity: .8;}
.notification_type_icon{display:none !important}
.selected .following,
.selected .followers,
.selected .settings,
.selected .posts,
.selected .messages,
.selected .drafts,
.selected .queue, 
.selected .likes {
	color: #A099A1 !important; 
	background: #bfaa8d !important;}
a.explore_more_tags,
a.add_and_remove,
a.customize,
a.mass_editor
	{background-color:#bfaa8d !important;  
	background-image:none !important;
	border:0px !important}
div.gradient,
div.dashboard_nav_item, 
a.permalink
	{background-color:#bfaa8d !important;
	opacity:.9; 
	background-image:none !important;
	border:0px !important}
.content.is_media, 
.mask.top, 
.mask.bottom {
	background: none !important; }
.post.photo_post .photo, 
.post.photo_post .video_thumbnail {
	border-radius: 16px !important;}
.post.photo_post .photo, 
.post.photo_post .video_thumbnail 
	{-moz-box-shadow: 1px 1px 3px #000000 !important;}
#auto_pagination_loader
	{background: #bfaa8d !important;}
div.return_to_top_icon{	background: none !important;
	width: 0px !important;
	height: 0px !important;
	margin-top: 0px !important;
	border-bottom: solid 30px white !important;
	border-right: 15px solid transparent !important;
	border-left: 15px solid transparent !important;}
#left_column #posts .notification {
background-color:#bfaa8d !important;
color:#666 !important;
border-bottom-color: #eee !important}
#left_column #posts .post .post_avatar{width: 64px !important; height: 64px !important; margin: 0px !important;}
#left_column #posts .post .sub_avatar_inner {
-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 32px !important; height: 32px !important; margin: 0px !important;}


.new_post_label{
font-size:10px !important;
}

.new_post_label_icon{background-image:center !important}

#new_post{
background-color:#fff !important;
}
img[src*="http://oi46.tinypic.com/21ca6ms.jpg"]{
    width:100;
    height:70px;
    padding-right:250px;
    opacity: 0;
}

#logo {height: 0 !important;
width: 0 !important;
padding-left: 209px !important;
padding-top: 42px !important;
background: url('http://oi46.tinypic.com/28vvjmf.jpg' ) no-repeat !important;}
.sub_avatar{width:32px !important; height:32px !important; background-color:transparent !important}
}
