css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org") {
	body {
		background-color: #2E2E2E !important;
		color: #FFFFFF !important;
		font-family: arial !important;
		padding-left: 5px;
		padding-right: 5px;
	}
	a.replylink {
		color: #71718D !important;
	}
	div.reply {
		background-color: #424242 !important;
	
		border: 1.5px solid #848484 !important;
		border-left: none;
		border-top: none;
	
		display: table;
		padding: 2px;
	}
	div.post div.postInfo span.subject {
		color: #FF9933 !important;
	}
	div.postingMode {
		background-color: #3399FF !important;
	}
	div.post div.postInfo span.postNum a {
		text-decoration: none;
		color: #FFFFFF !important;
    }
	blockquote > span.quote {
		color: #669999 !important;
	}
	div.post div.postInfo span.postNum {
	
	}
	div.post div.postInfo span.postNum a {
		text-decoration: none;
		color: #FFFFFF !important;
	}
	div.post div.postInfo span.postNum a:hover {
		color: #FFFFFF !important;
    }
	div.post div.postInfo span.nameBlock span.name {
		color: #99CC00 !important;
		font-weight: bold;
	}
	a, a:visited {
		color: #CCA300;
	}
	a.replylink, div#absbot a {
		color: #79CDCD !important;
		text-decoration: underline!important;
	}
	.useremail .name, .useremail .postertrip {
		color: #40D2AD !important;
	}
	div#boardNavDesktop a {
		font-weight: normal;
		padding: 1px;
		text-decoration: none;
		color: #40D2AD !important;
	}
	div.pagelist {
		font-size: 13px !important;
	
		margin: 0;
		adding: 3px 7px;
	
		float: left;
	
		border: none;
		background: #D6DAF0 !important;
	
		border-right: 1px solid #B7C5D9;
		border-bottom: 1px solid #B7C5D9;
	
		list-style: none;
		overflow: hidden;
	
		color: #89A;
	}
	div.boardBanner > img {
		border: 1px solid #FFFFFF !important;
	
		margin: 5px 0px 5px 0px;
	}
	div#boardNavDesktopFoot a {
		font-weight: normal;
		padding: 1px;
		text-decoration: none;
		color: #40D2AD !important;
	}
}
