css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.breekpunt.nl") {

/* Achtergrondkleur, tintje blauw. */
body {
background-color: #C4D1DD !important;
}
/* Correctie m.b.t. nieuwe achtergrond kleur:
    Afbeeldingen voor ronde hoeken verwijderen. */
span.corners-top, span.corners-bottom, span.corners-top span, span.corners-bottom span {
background: none transparent !important;
}
/* Ronde hoeken weer toevoegen. */
.headerbar, .navbar, .panel, .forabg, .forumbg, .post  {
border-radius: 8px !important;
}

/* Headerbar aanpassen, mooier maken. */
.headerbar {
background: -moz-linear-gradient(top, #1E5799 0%, #2989D8 50%, #207cca 51%, #7db9e8 100%) !important;
box-shadow: 4px 4px 4px #333 !important;
margin-bottom: 8px !important;
}
#site-description h1 {
text-shadow: 0px 0px 15px #fff !important;
}

/* Forum overzicht en fora aanpassen, mooier maken. */
.forabg, .forumbg {
background: -moz-linear-gradient(top, #07528C 3%, #73b1e7 25%, #539fe1 49%, #87bcea 51%, #539fe1 52%, #87bcea 55%, #539fe1 87%, #07528C 100%) !important;
box-shadow: 4px 4px 4px #000 !important;
margin-bottom: 8px !important;
}

/* Gradient toevoegen aan de navbar */
.navbar {
background: -moz-linear-gradient(top, #CADCEB 0%, #A1C4E0 50%, #92B1CC 51%, #d5ebfb 100%) !important;
box-shadow: 4px 4px 4px #333 !important;
margin-bottom: 8px !important;
}

/* Panel, binnenschaduw. */
.panel {
box-shadow: inset 2px 2px 2px !important;
}

/* Posts van gebruikers aanpassen. */

/* Citaten mooier. */
blockquote {
margin-right: 10px !important;
border: 1px solid #ccc !important;
-moz-border-radius: 8px !important;
background-color: transparent !important;
background-position: 0px !important;
background-image: -moz-linear-gradient(
    center bottom,
    rgb(252,248,197) 33%,
    rgb(255,254,245) 70%
) !important;
-moz-box-shadow: 2px 2px 2px #030303 !important;
}
blockquote cite {
padding-left: 12px !important;
width: 98% !important;
margin-left: 0px !important;
border: 1px solid #a3bacc !important;
-moz-border-radius: 8px !important;
color: #2e506c !important;
background-image: -moz-linear-gradient(
    center bottom,
    rgb(163,186,204) 9%,
    rgb(202,220,235) 55%
) !important;
}
/* Plaatsing van posts en profielen omdraaien. */
/* Posts moeten rechts. */
.postbody {
float: right !important;
}
/* Profielen moeten links. */
.postprofile {
width: 20% !important;
float: left !important;
border-left: 0px !important;
border-right: 1px solid #fff !important;
}
/* On-line markering moet nu ook naar links. */
.online {
background-position: 17% 0pt !important;
background-repeat: no-repeat;
}
/* Profielfoto opmaken. */
.postprofile img {
-moz-border-radius: 8px !important;
-moz-box-shadow: 2px 2px 4px #333 !important;
}

/* Links in reacties mooier. */
.postbody .content a {
border-bottom: 0px !important;
text-decoration: underline  !important;
}

.postbody .content a:hover {
color: #f00 !important;
border-bottom: 0px !important;
text-decoration: none  !important;
background-color: transparent !important;
}
/* Alternerende achtergrond kleuren van posts aanpassen. */
.bg1 {
background: -moz-linear-gradient(top, #FFFFFF 0%, #ECF3F7 2%, #ECF3F7 98%, #98BED1 100%) !important;
}
.bg2 {
background: -moz-linear-gradient(top, #FFFFFF 0%, #E1EBF2 2%, #E1EBF2 98%, #98BED1 100%) !important;
}

/* inputboxes en codes schaduw meegeven */
dl.codebox, .inputbox {
-moz-box-shadow: inset 2px 2px 6px #999 !important;
}


}
