css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.livejournal.com/portal/") {

/* fixes width */
div.UpdateJournal {
width: 98% !important;
padding: 0px 1px 0px 0px !important;
}

/* fixes inline table */
div.UpdateJournal table {
width: 100% !important;
background-color: #FFFFFF !important;
border: 0px !important;
}

/* fixes 'tags' input field */
input[name="prop_taglist"] {
width: 80% !important;
margin-right: 5px !important;
}

/* fixes spaces */
div.UpdateJournal br {
line-height: 0.25em !important;
}

}
