css:
@-moz-document url-prefix("http://filelist.ro"), url-prefix("http://flro.org") {

html, body{
    background:#E1E1E1!important;
}

.logo {
    width: 400px!important;
    height: 160px!important;
    background: url('http://i257.photobucket.com/albums/hh211/ioaneee/FilelistLogo-1.png') no-repeat!important;
    margin-top: 10px;
}

.statusbar{
    margin-top: 40px;
    line-height:15px!important;
}
.mainheader{
    height:220px!important;
}
.subheader{
    margin-top: -13px!important;
}
#navigation {
    width:975px!important; 	margin:-10px 2px!important;	-moz-box-shadow: none!important;	-webkit-box-shadow: none!important;
}
.cblock-content{
    padding-top:5px!important;
    padding-bottom:5px!important;
}
.cblock-top{
    margin-top:-10px!important;
}
.pager{
    margin-top:0px!important;
    padding-top:14px!important;
    margin-bottom:0px!important;
    padding-bottom:14px!important;
}

IMG[width="55"][height="65"]{
    width:55px!important;
    height:85px!important;
    padding-bottom: 5px!important;
}


#nav ul li{
    padding:5px!important;
    padding-top:0px!important;
}

#nav lihome { 
    display:none;
}

#nav ul li a { background: none;
    padding:8px 15px 15px 15px!important;
    color:#ececec!important;
}
#nav ul li a:hover, #nav ul lihome a:hover{
    color:#111!important;
    height:9px!important;
        background: rgb(251,251,251)!important; 
background: -moz-linear-gradient(top,  rgba(251,251,251,1) 0%, rgba(225,225,225,1) 100%)!important; 
