background: -o-linear-gradient(top,  rgba(251,251,251,1) 0%,rgba(225,225,225,1) 100%)!important; 
background: -ms-linear-gradient(top,  rgba(251,251,251,1) 0%,rgba(225,225,225,1) 100%)!important; 
background: linear-gradient(to bottom,  rgba(251,251,251,1) 0%,rgba(225,225,225,1) 100%)!important; 
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#fbfbfb', endColorstr='#e1e1e1',GradientType=0 )!important; 

    text-shadow: 1px 1px 1px #fff!important;
    -webkit-border-radius: 3px!important;
    -moz-border-radius: 3px!important;
    border-top-radius: 3px!important;
}


FONT[color="#2471ff"],
[href="messages.php"] > SPAN,
SPAN[style="font-size:11px;font-weight:bold;"]{
    color:#4EB7FF!important;
}
FONT[color="#9ff847"]{
    color:#00E639!important;
}
