css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://arstechnica.com/civis/") {
  
html,body {
  background-color: #111111 !important;
}

#masthead {
  background-color: #222222 !important;
}

/* User Panel */

#user-panel {
  color: #c9c9c9 !important;
}

#user-panel a {
  color: #a5a5a5 !important;
}

#user-panel a:visited {
  color: #a5a5a5 !important;
}

#user-panel a:hover {
  color: #bbbbbb !important;
}

#user-links ul li a:link,
#user-links ul li a:visited {
  color: #a9a9a9 !important;
}

#user-links ul li a:hover {
  color: #bbbbbb !important;
}

#user-links .private-messages {
  background: transparent url('data:image/png;base64,') no-repeat left !important;
}

#user-links .private-messages a .message-count {
  color: #ff5b00 !important;
}

#user-links .private-messages a:visited .message-count {
  color: #9e1e00 !important;
}

#user-links .private-messages a:hover .message-count {
  color: #ff9000 !important;
}

#user-links .logout {
  background: transparent url('data:image/png;base64,') no-repeat left !important;
}

#user-message a {
  color: #a5a5a5 !important;
}

#user-message a:visited {
  color: #a5a5a5 !important;
}

#user-message a:hover {
  color: #bbbbbb !important;
}

#user-message a .message-count {
  color: #a5a5a5 !important;
}

#user-message a:visited .message-count {
  color: #a5a5a5 !important;
}

#user-message a:hover .message-count {
  color: #bbbbbb !important;
}

/* Anonymous Panel */

#anonymous-panel {
  color: #c9c9c9 !important;
}

#anonymous-panel a {
  color: #a5a5a5 !important;
}

#anonymous-panel a:visited {
  color: #a5a5a5 !important;
}

#anonymous-panel a:hover {
  color: #bbbbbb !important;
}

#anonymous-links ul li a {
  color: #a9a9a9 !important;
}

#anonymous-links ul li a:visited {
  color: #a9a9a9 !important;
}

#anonymous-links ul li a:hover {
  color: #bbbbbb !important;
}

#anonymous-links .register {
  background: transparent url('data:image/png;base64,') no-repeat left !important;
}

#anonymous-links .login {
  background: transparent url('data:image/png;base64,') no-repeat left !important;
}

#anonymous-message a {
  color: #a5a5a5 !important;
}

#anonymous-message a:visited {
  color: #a5a5a5 !important;
}

#anonymous-message a:hover {
  color: #bbbbbb !important;
}

/* Userbar */

#userbar {
  background-color: #353535 !important;
  color: #aaaaaa !important;
}

#userbar ul li a {
  color: #aaa !important;
}

#userbar ul li a:hover {
  color: #bbb !important;
}

#userbar .user-bookmarks {
  background: transparent url('data:image/png;base64,') no-repeat left !important;
}

#userbar .user-drafts {
  background: transparent url('data:image/png;base64,') no-repeat left !important;
}

#userbar .user-threads {
  background: transparent url('data:image/png;base64,') no-repeat left !important;
}

#userbar .user-posts {
  background: transparent url('data:image/png;base64,') no-repeat left !important;
}

#userbar .user-control-panel {
  background: transparent url('data:image/png;base64,') no-repeat left !important;
}

#userbar #advanced-search-link a {
  color: #aaa !important;
}

#userbar #advanced-search-link a:hover {
  color: #aaa !important;
}

#userbar #keywords {
  background: transparent url('http://cdn.arstechnica.net/civis/ars/theme/images/header-search-bg.png') no-repeat left top;
  color: #aaa !important;
}

/* TODO:  Administration Links */

#administration-links {
  color: #aeb1b7;
}

#administration-links a {
  color: #34323a;
}

#administration-links a:hover {
  color: #64626b;
}

/* Forum Links */

#forum-links {
  color: #888 !important;
}

#forum-links a {
  color: #aaa !important;
}

#forum-links a:hover {
  color: #bbb !important;
}

/* Breadcrumb / Jumpbox */

.breadcrumb {
  color: #888 !important;
}

.breadcrumb a {
  color: #ff5b00;
}

.breadcrumb a:visited {
  /* color: #9e1e00; */
}

.breadcrumb a:hover {
  color: #ff9000;
}

.jumpbox .jumptext {
  color: #999 !important;
}

/* Content Wrappers */

#content {
  border-left: 1px solid #141414 !important;
  border-right: 1px solid #141414 !important;
}

.mod-notice { /*TODO*/
  border: 3px solid red;
  background-color: #FF9999;
}
.mod-notice a{
  color: black;
}

#content.all-borders {
  border: 1px solid #141414 !important;
}

/* Page Titles */

h2.title {
  color: #cacaca !important;
}

h2.title a {
  color: #aaa !important;
}

h2.title a:visited {
  color: #aaa !important;
}

h2.title a:hover {
  color: #bbb !important;
}

/* My Threads toggle */

a.toggle-posted { color: #9b9fa4 !important; }
a.toggle-posted.active{ color:  #39393f !important;}

/* Forum Rules */

.forumrules {
  color: #aaa !important;
}

.forumrules a:link {
  color: #bbb !important;
}

.forumrules a:visited {
  color: #bbb !important;
}

.forumrules a:hover {
  color: #bbb  !important;
}


/* Actionbars */

.actionbar {
  background: #222 !important;
}

.actionbar a {
  color: #fff;
}

.actionbar a:visited {
  color: #ddd;
}

.actionbar a:hover {
  color: #ccc;
}

.actionbar .board-actions,
.actionbar .topic-actions {
  background: #555 url('data:image/png;base64,') no-repeat right top !important;
}

.actionbar .board-actions .search-box label {
  color: #fff;
}

.actionbar .pager {
  color: #fff;
}

.actionbar .pager li.selected {
  background-color: #7f7f7f;
}

.actionbar .pager a {
  color: #ff5b00;
}

.actionbar .pager a:visited {
  color: #ff9000;
}

.actionbar .pager a:hover {
  color: #9e1e00;
}

.actionbar #search_keywords {
  background: transparent url('http://cdn.arstechnica.net/civis/ars/theme/images/header-search-bg.png') no-repeat left top;
  color: #9b9ea3;
}

.actionbar .board-actions #add_keywords {
  background: transparent url('http://cdn.arstechnica.net/civis/ars/theme/images/header-search-bg.png') no-repeat left top;
  color: #9b9ea3;
}

.actionbar .subscribe-actions {
  color: #fff;
}

.actionbar .subscribe-actions .subscribe a {
  background: transparent url('http://cdn.arstechnica.net/civis/ars/theme/../imageset/en/_subscribe.png') no-repeat left top;
}

.actionbar .subscribe-actions .mark-read a {
  background: transparent url('http://cdn.arstechnica.net/civis/ars/theme/../imageset/en/_mark_read.png') no-repeat left top;
}

.actionbar .subscribe-actions .bookmark a {
  background: transparent url('http://cdn.arstechnica.net/civis/ars/theme/../imageset/en/_bookmark.png') no-repeat left top;
}

.actionbar .subscribe-actions .check-all a {
  background: transparent url('http://cdn.arstechnica.net/civis/ars/theme/../imageset/en/_check_all.png') no-repeat left top;
}

.actionbar .subscribe-actions .uncheck-all a {
  background: transparent url('http://cdn.arstechnica.net/civis/ars/theme/../imageset/en/_uncheck_all.png') no-repeat left top;
}

/* Board List */

table.board-list th {
  background: #444 !important;
  border: 1px solid #000 !important;
  color: #fff;
}

table.board-list th.board-category {
  background-color: #666 !important;
}

table.board-list td {
  background-color: #222 !important;
  border: 1px solid #000 !important;
  color: #999  !important;
}

table.board-list td.board-info a {
  background-color: #222 !important;
}

table.board-list td.board-info a:hover {
  background-color: #181818 !important;
}

table.board-list td.board-info .board-title {
  color: #bbb !important;
}

table.board-list td.board-info .board-description {
  color: #888 !important;
}

table.board-list td.board-topics,
table.board-list td.board-posts {
  background-color: #888;
}

table.board-list td a {
  color: #bbb !important;
}

table.board-list td a:visited {
  color: #bbb !important;
}

table.board-list td a:hover {
  color: #bbb !important;
}

table.board-list .board-title a:link,
table.board-list .board-title a:visited {
  color: #bbb !important;
}

/* Topic List */

table.topic-list th {
  background-color: #444 !important;
  border: 1px solid #000 !important;
  color: #fff;
}

table.topic-list th a:link,
table.topic-list th a:visited {
  color: #262626;
}

table.topic-list th.last-post-head {
  border-right: 0 !important;
}

table.topic-list td {
  background: #333 !important;
  border: 1px solid #000 !important;
  color: #999 !important;
}

table.topic-list td.topic-title a:visited {
  color: #999 !important;
}

table.topic-list td.topic-title a:visited:hover {
  color: #ccc !important;
}

table.topic-list td.topic-title .pager {
  color: #595959;
}

table.topic-list td.topic-title .pager a {
  color: #ff5b00;
}

table.topic-list td.topic-title .pager a:visited {
  color: #9e1e00;
}

table.topic-list td.topic-title .pager a:hover {
  color: #ff9000;
}

table.topic-list td.topic-author {
  background-color: #eee;
}

table.topic-list td.topic-replies,
table.topic-list td.topic-views {
  background-color: #eee;
}

table.topic-list td a {
  color: #bbb !important;
}

table.topic-list td a:hover {
  color: #ccc !important;
}

/* Quickreply */
#qr_editor_table {
  color: #aaa !important;
}

#qr_editor_table textarea {
  background-color: #222 !important;
  border: 1px solid white !important;
}

/* TODO: quickmod */

#quickmod {
  background-color: #eee;
  color: #262626;
  border: 1px solid #ccc;
}

/* Polls */

table#poll {
  border-bottom: 1px solid #444 !important;
  background-color: #333 !important;
}

table#inner-poll a {
  color: #ff5b00;
  text-decoration: none;
}

table#inner-poll a:visited {
  color: #ff9000;
}

table#inner-poll a:hover {
  color: #ff9000;
}

/* Posts */

table.post {
  border-bottom: 1px solid #333 !important;
}

table.post.odd {
  background-color: #444 !important;
}

table.post.even {
  background-color: #333 !important;
}

table.post.spam, table.topic-list tr.spam td{
  background-color: #faa;
}

table.post .spam-options a{
  color: #999 !important;
}

table.post .post-author {
  background-color: #f5f5f5;
  color: #aaa !important;
}

table.post.even .post-author {
  background: #444 url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAATCAYAAACp65zuAAAATElEQVQoz5XSyxEAIAhDwUdN9F+b3hwVPwnnHQYIIFRmtlAQQCjoCWd0hTs6whMq8IYW+EID/hBAKMjraM1obW3d0UrGytr6HvUaBXcAHR5aAlysVQAAAABJRU5ErkJggg==') no-repeat right 20px !important;
}

table.post.odd .post-author {
  background: #333 url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAATCAYAAACp65zuAAAATElEQVQoz5XSyxEAIAhDwUdv9N+S3hwVPwnnHQYIIFRmtlAQQCjoCWd0hTs6whMq8IYW+EID/hBAKMjraM1obW3d0UrGytr6HvUaBXfBCCIjWMRKIAAAAABJRU5ErkJggg==') no-repeat right 20px !important;
}

table.post.spam .post-author { /*TODO*/
  background: #f99;
}

table.post .post-author .post-author-name a, table.post .post-author .post-author-name a:visited {
  color: #ccc !important;
}

table.post .post-author .post-author-title a {
  color: rgb(36, 175, 58) !important;
}

table.post .post-author .post-author-tribus,
table.post .post-author .post-author-registered,
table.post .post-author .post-author-postcount {
  color: #999 !important;
}

table.post .post-date-actions-inner {
  border-bottom: 1px dotted #777 !important;
}

table.post .post-date {
  color: #aaa !important;
}

table.post .post-date a {
  color: #999 !important;
}

table.post .post-date a:hover {
  color: #aaa !important;
}

table.post.unread .post-date {
  color: #999  !important;
}

table.post.unread .post-date a {
  color: #999 !important;
}

table.post .post-content,
#preview-content {
  color: #bbb  !important;
}

table.post .post-content a.embed-toggle {
  border: 1px solid #555 !important;
  background: #222 !important;
  color: #888 !important;
  text-shadow: 1px 1px 1px black !important;
}

table.post .post-content a.embed-toggle:hover {
  background-color: #CCC;
}

table.post .post-content a,
table.post .post-ignore a,
#preview-content a {
  color: #ff5b00;
}

table.post .post-content a:visited,
table.post .post-ignore a:visited,
#preview-content a:visited {
  color: #ff9000 !important;
}

table.post .post-content a:hover,
table.post .post-ignore a:hover,
#preview-content a:hover {
  color: #ff9000;
}

table.post .post-content .quotecontent,
table.post .post-content .ip-ubbcode-quote-content,
#preview-content .quotecontent,
#preview-content .ip-ubbcode-quote-content {
  border-bottom: 1px solid #666 !important;
  border-top: 1px solid #666 !important;
}

table.post .post-content .codecontent,
#preview-content .codecontent {
  font-family: monospace;
  border: 1px solid #aaa !important;
  background-color: #282828 !important;
}

table.post .post-content .posthilit,
#preview-content .posthilit {
  background-color: #008844 !important;
}

table.post .post-content .search-result-info,
#preview-content .search-result-info {
  color: #999 !important;
}

table.post .post-edited {
  color: #888 !important;
}

table.post .post-edited-inner {
  border-top: 1px dotted #888 !important;
}

table.post .post-edited a {
  color: #aaa !important;
}

table.post .post-edited a:visited {
  color: #aaa !important;
}

table.post .post-edited a:hover {
  color: #bbb !important;
}

table.post .post-ignore {
  color: #aaa !important;
}

table.post .post-ignore a {
  color: #bbb !important;
}

table.post .post-ignore a:visited {
  color: #bbb !important;
}

table.post .post-ignore a:hover {
  color: #ccc !important;
}

table.post .post-ignore strong a {
  color: #bbb !important;
}

table.post .post-ignore strong a:visited {
  color: #bbb !important;
}

table.post .post-ignore strong a:hover {
  color: #ccc !important;
}

/* Post Form */

#page-body .rules {
  color: #888 !important;
}

#page-body .rules a {
  color: #ff5b00;
}

#page-body .rules a:visited {
  color: #9e1e00;
}

#page-body .rules a:hover {
  color: #ff9000;
}

#page-body .moderators {
  color: #888 !important;
}

#page-body .moderators a {
  color: #ff5b00;
}

#page-body .moderators a:hover {
  color: #ff9000;
}

.postform-error {
  background-color: #311 !important;
  color: #b99 !important;
  border: 1px solid #b99 !important;
}

#draft-loader,
#load-drafts,
#preview {
  background-color: #333 !important;
  border: 1px solid #444 !important;
}

#draft-loader h3,
#load-drafts h3,
#preview h3 {
  border-bottom: 1px solid #555 !important;
  color: #aaa !important;
}

#preview-poll {
  color: #aaa;
}


#draft-loader-content,
#load-drafts-content {
  color: #aaa !important;
}

#draft-loader a,
#load-drafts a {
  color: #ff5b00;
}

#draft-loader a:visited,
#load-drafts a:visited {
  color: #ff9000;
}

#draft-loader a:hover,
#load-drafts a:hover {
  color: #9e1e00;
}

#post-box {
  background-color: #333 !important;
  border: 1px solid #444 !important;
}

#post-box h3 {
  border-bottom: 1px solid #555  !important;
  color: #aaa !important;
}

#subject-field #subject-label {
  color: #999 !important;
}

#subject-field #subject {
  border: 1px solid #444;
  color: #bbb !important;
  background-color: #222 !important;
}

#format-buttons input,
#format-buttons select {
  background-color: #222  !important;
  color: #bbb !important;
  border: 1px solid #666;
}

#smilies h3 {
  color: #25324d;
}

#post-howto {
  border-bottom: 1px solid #555 !important;
  border-top: 1px solid #555 !important;
}

#post-howto ul li {
  color: #999 !important;
}

#post-howto ul li a {
  color: #bbb !important;
}

#post-howto ul li a:visited {
  color: #bbb !important;
}

#post-howto ul li a:hover {
  color: #ccc !important;
}

#message-box textarea {
  border: 1px solid #767676;
  background-color: #222 !important;
  color: #ccc !important;
}

#submit-buttons {
  background-color: #222 !important;
}

#submit-buttons #draft-preview {
  background: #555 url('data:image/png;base64,') no-repeat right top !important;
  color: #9b9ea3;
}

#submit-buttons #draft-preview input {
  background: transparent;
  color: #fff;
}

#submit-buttons #submit {
  background: #555 url('data:image/png;base64,') no-repeat left top !important;
}

#submit-buttons #submit input {
  background: transparent url('http://cdn.arstechnica.net/civis/ars/theme/../imageset/en/_submit_post.png') no-repeat left top;
}

#options-panel h3,
#advanced-options-panel h3,
#poll-panel h3,
#attachment-panel h3 {
  color: #aaa !important;
}

#options-panel #options,
#advanced-options-panel #advanced-options,
#poll-panel #poll-options,
#attachment-panel #attachment-attachments,
#attachment-panel #attachment-options {
  background-color: #333 !important;
  border: 1px solid #555 !important;
  color: #999 !important;
}

#options-panel #options a,
#advanced-options-panel #advanced-options a,
#poll-panel #poll-options a,
#attachment-panel #attachment-attachments a,
#attachment-panel #attachment-options a {
  color: #ff5b00;
}

#options-panel #options a:visited,
#advanced-options-panel #advanced-options a:visited,
#poll-panel #poll-options a:visited,
#attachment-panel #attachment-attachments a:visited,
#attachment-panel #attachment-options a:visited {
  color: #9e1e00;
}

#options-panel #options a:hover,
#advanced-options-panel #advanced-options a:hover,
#poll-panel #poll-options a:hover,
#attachment-panel #attachment-attachments a:hover,
#attachment-panel #attachment-options a:hover {
  color: #ff9000;
}

#review h3 {
  border-bottom: 1px solid #555 !important;
  color: #aaa !important;
}

#review #review-content {
  border-left: 1px solid #444 !important;
  border-right: 1px solid #444 !important;
  border-top: 1px solid #444 !important;
}

#toplink {
  border-top: 1px solid #444 !important;
}

#toplink a {
  color: #999 !important;
}

#toplink a:visited {
  color: #999 !important;
}

#toplink a:hover {
  color: #999 !important;
}

/* Online Users List */
/* TODO:  Is this ever visible? */

#users-online {
  border: 1px solid #d8d8d8;
}

#users-online-title {
  background-color: #9b9ea3;
  border-top: 1px solid #fff;
  color: #fff;
}

#users-online-body {
  background-color: #f5f5f5;
  border-bottom: 1px solid #fff;
  border-top: 1px solid #fff;
  color: #262626;
}

#users-online-body a {
  color: #34323a;
}

#users-online-body a:visited {
  color: #806f7f;
}

#users-online-body a:hover {
  color: #64626b;
}

#users-online-body a.username-coloured {
  color: #ff5b00;
}

#users-online-body a.username-coloured:visited {
  color: #9e1e00;
}

#users-online-body a.username-coloured:hover {
  color: #ff9000;
}

/* Latest Stories */
/* TODO: When this returns */

#latest-stories h2 {
  color: #262626;
}

#latest-stories ul li .category a {
  color: #ff5b00;
}

#latest-stories ul li .category a:visited {
  color: #9e1e00;
}

#latest-stories ul li .category a:hover {
  color: #ff9000;
}

#latest-stories ul li .title h3 a {
  color: #34323a;
}

#latest-stories ul li .title h3 a:visited {
  color: #806f7f;
}

#latest-stories ul li .title h3 a:hover {
  color: #64626b;
}

/* Footer */

#footer {
  color: #888  !important;
}

#footer a {
  color: #aaa !important;
}

#footer a:hover {
  color: #aaa !important;
}

#footer .notice {
  color: #777 !important;
}

#footer .notice a {
  color: #999 !important;
}

#footer .notice a:hover {
  color: #aaa !important;
}

/* image replacement stuff */
img[src="http://cdn.arstechnica.net/civis/ars/theme/images/logo.png"] {
  padding-left: 242px;
  padding-bottom: 46px;
  overflow: hidden;
  height: 0px;
  width: 0px;
  background: url("data:image/png;base64,")
    no-repeat top left;
}

img[src="http://static.arstechnica.net/forums/forum-powered.png"] {
  padding-left: 279px;
  padding-bottom: 99px;
  overflow: hidden;
  height: 0px;
  width: 0px;
  background: url("data:image/png;base64,")
    no-repeat top left;
}

#content.form h3 {
  background-color: #39393F;
  color: #fff;
}

#content.form hr.dashed {
  border-bottom: 1px dashed black;
}
}
