css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org") {

	body {
		background: url("http://i361.photobucket.com/albums/oo55/Saiden_v_Saidar/1a33faddb3d1716f6536f9267f0268a7-1.png") bottom right no-repeat fixed #000 !important;
		color: #eee !important;
		font-size:14px !important;
	}
	
	.globalMessage {
		padding-top: 20px;	
	}
	
	#boardNavDesktop {
		position: fixed !important;
		left: 0 !important;
		right: 0 !important;
		top: 0 !important;
	}
	
	h1,h2,h3,a[title*="Permanent link to "]:hover,.permalink,.highlight,.replyhl,#watchedthreadsdraghandle,.replymode,.postblock,input:not([type="submit"]):not([type="button"]):not([type="reset"]),select,textarea,th[bgcolor="#e04000"],a img.thumb,a img[src*="/thumb/"],.tn_reply,.tn_thread {
		-moz-border-radius: 6px !important;
		border-width: 1.5px !important;
		border-style: solid !important;
		border-color: #777777 !important;
		background:url("http://i361.photobucket.com/albums/oo55/Saiden_v_Saidar/1a33faddb3d1716f6536f9267f0268a7-1_.png") bottom right no-repeat fixed #444444 !important;
	}

	.replymode,.postblock,input:not([type="submit"]):not([type="reset"]),select,textarea,th[bgcolor="#e04000"]{
		background:url("http://i361.photobucket.com/albums/oo55/Saiden_v_Saidar/1a33faddb3d1716f6536f9267f0268a7-1_.png") bottom right no-repeat fixed #444444 !important;
	}

	.content,.reply,#watchedthreads,.nigratrain {
		background-color:#ffffff !important;
		background: url("http://i361.photobucket.com/albums/oo55/Saiden_v_Saidar/1a33faddb3d1716f6536f9267f0268a7-1_.png") bottom right no-repeat fixed #343434  !important;
	}

	.postingMode, .bf, blockquote embed, a[href*="affiliates.jlist.com"], img[src*="http://static.4chan.org/support/"], #recaptcha_logo, #recaptcha_tagline, .pagelist > div.prev, .pagelist > div.next {
		display: none !important;
	}

    
	div.hide_reply_button {
		display: block !important;
	}

	.postarea form div {
		border: none !important;
	}

	div.postarea, div.pagelist div.pages, div.pagelist {
		padding: 0 !important;
	}

	* {
		font: 10pt "Verdana";
	}

	a {
		color: #efefef !important;
	}

	hr {
		display: none !important;
	}

	#recent-images li {
		background-color: transparent !important;
	}
	
	#boardNavDesktop, #boardNavDesktopFoot, #boardNavMobile {
		background: url("http://i361.photobucket.com/albums/oo55/Saiden_v_Saidar/1a33faddb3d1716f6536f9267f0268a7-1.png") bottom right no-repeat fixed #000 !important;
		color: #000 !important;
		width: 100%;
		text-align: center;
		height: 12pt;
		font-size: 7pt !important;
	}
	
	#boardNavMobile {
		color: #fff !important;
		border-bottom: none !important;
	}

	#boardNavDesktop *, #boardNavDesktopFoot * {
		display: inline;
	}

	#boardNavDesktop *, #boardNavDesktopFoot *,	#boardNavMobile * {
		color: #eee;
		font-size: 7pt;
	}


	#boardNavDesktop a:hover, #boardNavDesktopfoot a:hover {
		background: #555;
	}

	form {
		margin: 1em;
	}

	.inputtext, textarea {
		width: 295px;
	}

	.rules {
		display: none;
	}

	table.postForm > tbody > tr > td:first-child, .qrHeader {
		-moz-border-radius: 6px !important;
		border-width: 1.5px !important;
		border-style: solid !important;
		border-color: #777777 !important;
		background:url("http://i361.photobucket.com/albums/oo55/Saiden_v_Saidar/1a33faddb3d1716f6536f9267f0268a7-1_.png") bottom right no-repeat fixed #444444 !important;

	}

	h1, h2, h3, h4 {
		-moz-border-radius: 6px !important;
		border-width: 1.5px !important;
		border-style: solid !important;
		border-color: #777777 !important;
		background:url("http://i361.photobucket.com/albums/oo55/Saiden_v_Saidar/1a33faddb3d1716f6536f9267f0268a7-1_.png") bottom right no-repeat fixed #444444 !important;

	}
	
	dt, .boxcontent, #updater {
		color: #fff !important;
	}

	.menubutton {
		background-image: none !important;
	}

 	#updater:hover, div#thread_filter.autohide:hover, .box-outer, #preview-tooltip-nws, #preview-tooltip-ws, #disclaimer-dialog, .yuimenu .bd, .reply, #backwash_tooltip {
		background-color: #333 !important;
		border: 1px solid #555 !important;
		color: #eee !important;
		border-radius: 8px;
	}

	.post.reply.highlight {
		background-color: #4B4B4B !important;
		border: 1px solid #4B4B4B !important;
		color: #eee !important;
	}

	.warning, .capcodeMod .name {
		color: gray !important;
	}

	.nameBlock .capcode, .postertrip, .filesize, span.filetitle, .filesize a, a.linkmail, a.quotejs, span.subject, .nameblock .name, .omittedposts, .abbr, a.useremail .name, .name {
		color: #fff !important;
		font-size: 8pt;
	}
	
	.capcodeAdmin .name {
		color: #a55 !important;
	}
	
	span.subject {
		color: #e15014 !important;
	}

	#hd, small, font[size="1"], font[size="2"], #ft, #option-button, #navtopr, .boardBanner, .sideArrows, img.rotating, .stylechanger {
		display: none !important;
	}

	.pagelist {
		background: #444 !important;
		border: 1px solid #555 !important;
	}

	.pagelist > div {
		font-size: 8pt;
	}

	.pagelist > div a, .pagelist > div strong {
		color: #eee !important;
	}

	.pagelist > div strong {
		background-color: #000;
	}

	.pagelist > div input {
		display: none !important;
	}

	.pagelist > div:first-child {
		display: none;
	}

	pre, textarea, input:not([type="button"]):not([type="checkbox"]):not([type="submit"]):not([type="reset"]), select, input[type="file"] > input[type="text"] {
		-moz-appearance: none;
		-webkit-appearance: none;
		font-size: 9pt ! important;
		color: #fff !important;
		background: #333 !important;
		border-color: gray !important;
		border: 1px solid;
	}

	#filter-container .yuimenuitem-selected {
		border-color: gray !important;
	}

	.boxbar, #preview-tooltip-nws h2, #preview-tooltip-ws h2, #disclaimer-dialog h2, .hd {
		background-color: transparent !important;
	}

	dd {
		color: #fff !important;
		border-bottom: 1px gray solid !important;
	}

	input[type="submit"], input[type="button"], select {
		background: #444 !important;
		color: #aaa !important;
		border: 1px solid gray;
		-moz-appearance: none;
		-webkit-appearance: none;
		font-size: 9pt !important;
	}

	input[type="checkbox"] {
		-moz-appearance: none !important;
		-webkit-appearance: none !important;
		background: rgba(0,0,0,.4) !important;
		border: 1px solid rgba(255,255,255,.3) !important;
		height: 10px !important;
		width: 10px !important;
		border-radius: 2px !important;
	}

	.reply input[type="checkbox"] {
		border: 1px solid rgba(255,255,255,.3) !important;
	}

	input[type="checkbox"]:checked {
		background: rgba(255,255,255,.4) !important;
	}
	
	div[style="width: 100%; font-size: 4px; background-color: rgba(224,64,0,1);"] {
		display: none !important;
	}

	th {
		background: #222 !important;
	}

	th font {
		font-size: 7pt;
	}

	td.deletebuttons {
		font-size: 8pt !important;
		color:#eee !important;
	}
	
	img#recaptcha_reload,
	img.refreshBtn {
		height: 0px !important;
		padding-top: 18px !important;
		width: 25px !important;
		background-image: url(data:image/png;base64,) !important;
	}

	img#recaptcha_switch_audio {
		height: 0px !important;
		padding-top: 15px !important;
		width: 25px !important;
		background-image: url(data:image/png;base64,) !important;
	}
	
	img#recaptcha_whatsthis {
		height: 0px !important;
		padding-top: 16px !important;
		width: 25px !important;
		background-image: url(data:image/png;base64,) !important;
	}

	.pagelist, .pagelist div {
		width: 100% !important;
	}

	.pagelist .pages {
		padding: 1px 5px;
		text-align: center;
	}	
	
	.new {
		background: rgba(255,255,255,.4) !important;
		border-radius: 5px;
	}

	.postarea  td.recaptcha_image_cell, #quickrep td.recaptcha_image_cell,  td[style="padding-left: 7px;"] {
		padding: 0 !important;
	}

	#recaptcha_image img {
		opacity: 0.8;
	}

	.recaptchatable #recaptcha_response_field, #quick_recaptcha_response_field {
		margin-top: 3px !important;
	}

	.recaptcha_input_area {
		padding: 3px 0 0 0 !important;
	}

	tr[height="73"], tr[height="73"] td {
		height: 59px !important;
	}
}

@-moz-document domain("dis.4chan.org") {
	.thread, .newthread, .head {
		background-color:#333 !important;
		border: 1px solid #555 !important;
		color: #eee !important;
		border-radius: 8px;
	}

	.border, .hborder {
		background: none !important;
		border: none !important;
	}
}
