@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rutube.ru") {

#user_menu, #nav, #top-main,
#vcard, #adriver_banner,
#footer-container,
.corn_t, .corn_b,
.plMenu, .tagscloud ,
.cont>div{
    display:none !important;
}
#pid{
    display:block !important;
}

#header{
    width:92% !important;
    height:0px !important;
    padding:5px !important;
}
.main_search{
    top:-3px !important;
}
.main_search .search_text, .main_search .search_text_focus{
    float:right;    
    position: absolute;
    right: 187px;
    width: 30% !important;
    z-index:1 !important;
}
.h-logo{
    position:absolute !important;
    right:0px !important;
    top:5px !important;
}
.wrapper,.wcont, #player, .cont,.pid {
    width:99% !important;
} 
.lCol{
    width:78% !important;
}
.player{
     padding-left:5px  !important;
}
.player h1{
    position: fixed;
    top: 2px;
    border:0;
}
#garmoshka{
    margin-top:35px !important;
}

}
