css:
/* --- Sorozatjunkie redesign (by garky) ------------- */
/* --- Website: garky.freeblog.hu -------------------- */
/* --- Please do not modify this script! ------------- */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("sorozatjunkie.hu") {

div#felulrebox{
display:none !important;
}

/* top 100px margin fix */
html>body>div {
/*
margin: 0px auto !important;
border-left: 10px solid #4e4e4e !important;
border-right: 10px solid #4e4e4e !important;
-moz-box-shadow: 0px -3px 2px 2px !important;
-webkit-box-shadow: 0px -3px 2px 2px !important;
box-shadow: 0px -3px 2px 2px !important; 
*/
}

body {
background: url("http://i.imgur.com/5XgF3.png") repeat fixed 0% 0%  #575757 !important;
}

a {
color: #125390 !important;
}
a:hover {
text-decoration: underline !important;
}

/* Header & page */
#giganagy {
background: #fff url("http://i.imgur.com/296VN.jpg")  no-repeat scroll 0 0 !important;
border-left: 10px solid #4e4e4e !important;
border-right: 10px solid #4e4e4e !important;
-webkit-box-shadow: 0px 5px 2px 2px #000  !important;
-moz-box-shadow: 0px 5px 2px 2px #000  !important;
box-shadow: 0px 5px 2px 2px #000  !important;
}
.folink {
position: absolute !important;
top: -210px !important;
left: -520px !important;
width: 495px !important;
height: 80px !important;
}

/* Header Ad border */
body > div:nth-child(3) {
border-left: 10px solid #4e4e4e !important;
border-right: 10px solid #4e4e4e !important;
-webkit-box-shadow: 0px 0px 2px 2px #000 !important;
-moz-box-shadow: 0px 0px 2px 2px #000 !important;
box-shadow: 0px 0px 2px 2px #000 !important; 
}

/* Search box */
#keresoelhelyezes {
margin-top: 60px !important;
margin-left: 40px !important;
position: absolute !important;
}

#s {
border: 2px solid #4e4e4e  !important;
-webkit-border-radius: 20px  !important;
-moz-border-radius: 20px  !important;
border-radius: 20px  !important;
padding-left: 22px !important;
background: url("http://i.imgur.com/uiHDw.png") no-repeat scroll 5px 3px transparent !important;
-webkit-box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.15) inset, 0px 1px 0px rgba(255, 255, 255, 0.8) !important;
-moz-box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.15) inset, 0px 1px 0px rgba(255, 255, 255, 0.8) !important;
box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.15) inset, 0px 1px 0px rgba(255, 255, 255, 0.8) !important;
-moz-appearance: none !important;
}
#s:focus {
border: 2px solid #ae0407 !important;
outline: none !important;
}
#searchbutton {
display: none  !important;
}

/* login link */
#keresoelhelyezes a {
color: #ccc !important;
text-decoration: none !important;
}
#keresoelhelyezes a:hover {
color: #ae0407 !important;
}

/* Menu */
.menu {
margin-top: 60px !important;
background: #4e4e4e !important;
letter-spacing: 1.2px !important;
font-size: 13px !important;
color: #fff !important;
border-top: 2px solid #4e4e4e !important;
border-bottom: 2px solid #4e4e4e !important;
text-align: center !important;
}

.menu:hover, .menu:hover a {
background: transparent  !important;
color: #000  !important;
}

.menu a  {
color: #fff !important;
font-family: Helvetica, Arial, sans-serif !important;
text-decoration: none !important;
}

.menu a:hover  {
color: #ae0407 !important;
}

/* Sidebar */

.sidebarkiemelt a, #siderbar h3, #kategoriakjobbra h3, .menukoldalt h3 {
color: #ae0407 !important;
font-family: Helvetica, Arial, sans-serif !important;
}

.sidebarkiemelt h3:before {
content: url("http://i.imgur.com/8K52Y.png")!important;
padding-right: 5px !important;
vertical-align: top !important;
}

.sidebarkiemelt a:hover {
text-decoration: none !important;
}

.sidebaridezet {
background: url("http://i.imgur.com/EJRu1.png") no-repeat scroll 8px -1px transparent !important;
}

#kategoriakjobbra a, .menukoldalt a, .sidebaridezet a {
color: #4e4e4e !important;
}
#kategoriakjobbra a:hover, .menukoldalt a:hover, .sidebaridezet a:hover {
color: #ae0407 !important;
text-decoration: none !important;
}

#kategoriakjobbra h3:before {
content: url("http://i.imgur.com/QXCmz.png")!important;
padding-right: 3px !important;
vertical-align: top !important;
}

/* Border fix */


#bejegyzesek {
padding: 0 18px 0 0 !important;
float: left !important;
border-right: 2px solid #e2e2e2 !important;
}

#sidebar {
border-left: 2px solid #e2e2e2 !important;
width: 400px !important;
float: left !important;
margin-left: -2px !important;
position: relative !important;
background: transparent !important;
}

/* Spoilerkarantén */
.menukoldalt h3:before {
content: url("http://i.imgur.com/lvgp2.png")!important;
padding-right: 3px !important;
vertical-align: top !important;
}

/* Archívum */
.menukoldalt ul h3:nth-child(1):before {
content: url("http://i.imgur.com/t8LqH.png")!important;
padding-right: 3px !important;
vertical-align: top !important;
}

/* Entry */

/* Entry title */
#bejegyzesek h2 a {
color: #ae0407 !important;
}
#bejegyzesek h2 a:hover {
color: #ae0407 !important;
text-decoration: none !important;
}

/* Entry links */
#bejegyzesek a {
color: #ae0407 !important;
text-decoration: none !important;
}
#bejegyzesek a:hover {
text-decoration: underline !important;
}

#bejegyzesek .date, #bejegyzesek .date a, #bejegyzesek .author {
color: #333 !important;
font-size: 13px !important;
margin-top: 3px !important;
font-weight: normal  !important;
}

.date:before {
content: url("http://i.imgur.com/BjZkH.png")!important;
padding-right: 5px !important;
vertical-align: middle !important;
}

/* Footer */

.footercuccok {
border-top: 2px solid #e2e2e2!important;
}

.footercuccok div {
margin-bottom: 20px  !important;
}

.footercuccok:after {
content: 'Redesign/Userstyle: garky.freeblog.hu'  !important;
}

.footercuccok a {
color: #ae0407 !important;
border: none !important;
}

.footercuccok a:hover {
text-decoration: underline !important;
}

/* Read more link */
.tovabb, .more-link {
font-weight: normal !important;
}

/* Pagination */

#bejegyzesek .previous {
color: #fff !important;
background: #4e4e4e url("http://i.imgur.com/bCFHy.png") no-repeat 100px 2px !important;
padding: 5px !important;
-webkit-border-radius: 8px !important;
-moz-border-radius: 8px !important;
border-radius: 8px !important;
-webkit-box-shadow: -1px 2px 2px 0px rgba(0, 0, 0, 0.2)  !important;
-moz-box-shadow: -1px 2px 2px 0px rgba(0, 0, 0, 0.2)  !important;
box-shadow: -1px 2px 2px 0px rgba(0, 0, 0, 0.2)  !important;

}
#bejegyzesek .previous:hover {
background: #ae0407 url("http://i.imgur.com/bCFHy.png") no-repeat 100px 2px !important;
color: #fff !important;
}

#bejegyzesek .previous a {
padding-left: 10px !important;
padding-right: 20px !important;
color: #fff !important;
text-decoration: none !important;
}

#bejegyzesek .next {
color: #fff !important;
background: #4e4e4e url("http://i.imgur.com/VR0qc.png") no-repeat 5px 2px !important;
padding: 5px !important;
-webkit-border-radius: 8px !important;
-moz-border-radius: 8px !important;
border-radius: 8px !important;
-webkit-box-shadow: 1px 2px 2px 0px rgba(0, 0, 0, 0.2)  !important;
-moz-box-shadow: 1px 2px 2px 0px rgba(0, 0, 0, 0.2)  !important;
box-shadow: 1px 2px 2px 0px rgba(0, 0, 0, 0.2)  !important;
margin-right: 270px !important; /* Pushes prev button right when next is available */
         
}
#bejegyzesek .next:hover {
background: #ae0407 url("http://i.imgur.com/VR0qc.png") no-repeat 5px 2px !important;
color: #fff !important;
}

#bejegyzesek .next a {
padding-left: 20px !important;
padding-right: 10px !important;
color: #fff !important;
text-decoration: none !important;
}

/* Hide empty spans */
.next:empty, .previous:empty {
display: none  !important;
}

/* Comments */

.eddigikommentek {
border-top: 5px solid #4e4e4e !important;
border-bottom: 5px solid #4e4e4e !important;
}
.kommentalja {
background: #F9F9F9 !important;
border: 1px solid rgba(78, 78, 78, 0.3) !important;  
}
.amitirt {
background: #F9F9F9 !important;
-webkit-border-radius: 1px 1px 10px 10px  !important;
-moz-border-radius: 1px 1px 10px 10px  !important;
border-radius: 1px 1px 10px 10px  !important;
border: 1px solid rgba(78, 78, 78, 0.3) !important;
-webkit-box-shadow: 0px 1px 1px 0px #ccc  !important;
-moz-box-shadow: 0px 1px 1px 0px #ccc  !important;
box-shadow: 0px 1px 1px 0px #ccc  !important;
}
.amitirt2 {
background: #EAEAEA !important;
-webkit-border-radius: 1px 1px 10px 10px  !important;
-moz-border-radius: 1px 1px 10px 10px  !important;
border-radius: 1px 1px 10px 10px  !important;
border: 1px solid rgba(78, 78, 78, 0.3) !important;
color: #000 !important;
-webkit-box-shadow: 0px 1px 1px 0px #ccc  !important;
-moz-box-shadow: 0px 1px 1px 0px #ccc  !important;
box-shadow: 0px 1px 1px 0px #ccc  !important;
}

textarea#comment {
font-size: 13px !important;
font-family: Arial, Helvetica, sans-serif !important;
width: 475px !important;
resize: none !important;
border: 1px solid #4e4e4e !important;
-webkit-border-radius: 5px !important;
-moz-border-radius: 5px !important;
border-radius: 5px !important;
-moz-appearance: none  !important;
padding: 4px !important;
}
#comment:focus {
outline: none !important;
border: 1px solid #ae0407 !important;
}

.kommmentelogep {
border: 1px solid #4e4e4e !important;
-webkit-border-radius: 5px !important;
-moz-border-radius: 5px !important;
border-radius: 5px !important;
-webkit-box-shadow: 0px 0px 3px 0px #ccc  !important;
-moz-box-shadow: 0px 0px 3px 0px #ccc  !important;
box-shadow: 0px 0px 3px 0px #ccc  !important;
}

.kommmentelogep label {
font-size: 14px !important;
}


input#author, input#email {
font-size: 13px !important;
font-family: Arial, Helvetica, sans-serif !important;
border: 1px solid #4e4e4e !important;
-webkit-border-radius: 5px !important;
-moz-border-radius: 5px !important;
border-radius: 5px !important;
-moz-appearance: none  !important;
width: 200px  !important;
padding: 4px !important;
}

input#author:focus, input#email:focus {
border: 1px solid #ae0407  !important;
outline: none !important;
}

.button {
background: #4e4e4e !important;
color: #fff !important;
-webkit-border-radius: 5px !important;
-moz-border-radius: 5px !important;
border-radius: 5px !important;
padding: 3px !important;
margin-top: 5px !important;
}
.button:hover {
background: #ae0407 !important;
}

/* Contact */
#wpcf_your_name, #wpcf_email, #wpcf_response, #wpcf_usersubject, #wpcf_msg {
background: #F9F9F9 !important;
border: 1px solid #4e4e4e !important;
-webkit-border-radius: 5px !important;
-moz-border-radius: 5px !important;
border-radius: 5px !important;
-moz-appearance: none !important;
}

#wpcf_your_name:focus, #wpcf_email:focus, #wpcf_response:focus, #wpcf_usersubject:focus, #wpcf_msg:focus {
border: 1px solid #ae0407 !important;
outline: none !important;
}
#wpcf_msg {
width: 275px !important;
}

#contactsubmit {
background: #4e4e4e !important;
color: #fff !important;
-webkit-border-radius: 5px !important;
-moz-border-radius: 5px !important;
border-radius: 5px !important;
padding: 3px !important;
}
#contactsubmit:hover {
background: #ae0407 !important;
cursor: pointer !important;
}
/* --- end of style --- */
}
