css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document url(http://www.google.com/),

url-prefix(http://www.google.com/webhp?),

url-prefix(http://www.google.com/search?),

url(http://images.google.com/),

url-prefix(http://images.google.com/imghp?),

url-prefix(http://images.google.com/images?),

url-prefix(http://images.google.com/imgres?)

{



body {

  background-color: #222 !important;

  color: rgba(255,255,255,.5) !important;

  }

html > body > center > br + img[width="276"][height="110"] {

  background: url("http://i48.tinypic.com/15qunw7.jpg[/IMG]") center center no-repeat !important;

  width: 0 !important;

  height: 110px !important;

  padding-left: 276px !important;

  }

a#logo {

  color: transparent !important;

  }

a#logo > span,div#navbar td div,.ti td img {

  background-image: url("http://i48.tinypic.com/15qunw7.jpg[/IMG]") !important;

  }

.ti td img {

  width: 0 !important;

  height: 105px !important;

  padding-left: 150px !important;

  }

.gbh {

  border-top-color: rgba(0,130,255,.25) !important;

  }

a {

  color: rgba(255,255,255,.5) !important;

  outline: none !important;

  }

a:hover {

  color: rgba(0,130,255,.7) !important;

  }

a:hover:active {

  color: rgb(0,130,255) !important;

  }

span.gb1 > b {

  color: rgb(0,130,255) !important;

  }

input {

  -moz-appearance: none !important;

  background-color: transparent !important;

  border: 1px solid !important;

  -moz-border-radius: 3px !important;

  margin: 1px !important;

  }

input[title~="Search"],input[type=text] {

  color: rgb(0,130,255) !important;

  border-color: rgba(0,130,255,.5) !important;

  padding: 2px !important;

  }

input[title~="Search"]:focus,input[title~="Search"]:hover,

input[type=text]:focus,input[type=text]:hover {

  border-color: rgba(0,130,255,.7) !important;

  }

input[type="submit"] {

  color: rgba(255,255,255,.5) !important;

  border-color: rgba(255,255,255,.5) !important;

  padding: 0px !important;

  }

input[type="submit"]:hover {

  color: rgb(0,130,255) !important;

  border-color: rgba(0,130,255,.75) !important;

  }

input[type="submit"]:hover:active {

  border-color: rgb(0,130,255) !important;

  }

.t {

  background-color: transparent !important;

  border-width: 1px 0 !important;

  border-style: solid !important;

  border-color: rgba(0,130,255,.5) !important;

  }

table.t.bt td,table.ra td,.g,div.std,font,div.g td,h2,nobr,.j,#res td {

  color: rgba(255,255,255,.5) !important;

  }

span.a,span.i {

  color: rgba(0,130,255,.75) !important;

  }

#rhsline + td {

  background-color: rgba(0,130,255,.5) !important;

  }

table.t.bt + div + center > table > tbody > tr > td,.gb2 a,.ta {

  background-color: transparent !important;

  }

table.ra {

  background-color: #222 !important;

  }

#gbi {

  background-color: transparent !important;

  border: 1px solid rgba(0,130,255,.5) !important;

  border-top-color: transparent !important;

  }

.gb2 > div {

  border-top-color: rgba(0,130,255,.5) !important;

  }



}





@-moz-document url-prefix(http://images.google.com/imgres?)

{



td {

  color: rgba(255,255,255,.5) !important;

  background-color: transparent !important;

  }

a > font:hover {

  color: rgba(0,130,255,.7) !important;

  }

a > font:hover:active {

  color: rgb(0,130,255) !important;

  }

a > font + img {

  display: none !important;

  }



}
