css:
@-moz-document domain(tumblr.com) {
/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: #ffffff url('http://i1230.photobucket.com/albums/ee498/QuotesTVD/tumblr1.png') center center no-repeat !important;
}
.new_post_label_icon {
	background-image: none !important;
}


img[src*="https://secure.assets.tumblr.com/images/post_icons_sprite.png"],
.new_post_label_icon {
    opacity: 0 !important;
}

.new_post_label {
  opacity: 1 !important;
}

ol#posts .post.new_post .new_post_label {
    display: block !important;
}

.new_post_label{

font-size:10px !important;
padding-top:70px !important;
font-family:georgia !important;
font-style:bold !important;
text-transform: uppercase !important;
color:#fff !important;
letter-spacing:1px !important
}
}
