@-moz-document domain("boards.4chan.org"),domain("sys.4chan.org") {
html{
display:table;
position:relative !important;
clear:both !important;
max-width:100% !important;
height:100% !important;}

body{
width:100% !important;
position:relative !important;
clear:both !important;
display:table-cell;}

span#navlinks{
margin-top:8px !important;}

table#postForm.postForm.hideMobile{
z-index:5;
float:left !important;
position:fixed;
top:180px !important;}

.qrForm table#postForm.postForm.hideMobile:before{
content:"";
display:none !important;}

.qrForm table#postForm.postForm.hideMobile{
position:static !important;}

.qrHeader{
background:#222 !important;
color:#006fee !important;
}

table#postForm.postForm.hideMobile:before{
content:"Post";
font-size:30pt !important;
display:block !important;
color:#0077ff !important;
font-weight:bold !important;}

div.stylechanger.desktop,div.stylechanger.desktop,div#absbot.absBotText,div.pagelist input,div.prev,div#globalMessage,div#boardNavDesktopFoot,div.postingMode,div#imgControls,hr,table#postForm.postForm.hideMobile:hover:before,ul.rules,table#postForm.postForm.hideMobile tbody{
display:none !important;}

div#boardNavMobile{
z-index:5 !important;
border-radius:0 !important;}

div#boardNavMobile,div#settingsBox{
background:rgba(0,0,0,0.8) !important;
border:solid 1px #aaa !important;
box-shadow:0 0 10px rgba(0,160,255,0.5);
border-radius:5px;}
 table#postForm.postForm.hideMobile:hover tbody{
display: block !important;
}

div#boardNavMobile,.qrWindow,.qrForm table#postForm.postForm.hideMobile tbody, table#postForm.postForm.hideMobile:hover tbody{
background:rgba(0,0,0,0.8) !important;
border:solid 1px #aaa !important;
box-shadow:0 0 10px rgba(0,160,255,0.5);
border-radius:5px;}

.qrForm table#postForm.postForm.hideMobile tbody{
background:none !important;
border:none !important;
box-shadow:none !important;}

td.action.postblock,td.poster.postblock,td.title.postblock,div.postblock,table#postForm.postForm.hideMobile tbody tr td:first-child{
background:rgba(0,0,0,0.3) !important;
color:#ccc !important;
border:1px solid #555 !important;}

div.postblock{
text-align:center !important;
}

table#recaptcha_table.recaptchatable.recaptcha_theme_clean tbody{
box-shadow:none !important;
border-color:#000 !important;
border:none !important;}

span#count.new,table#recaptcha_table.recaptchatable.recaptcha_theme_clean tbody tr td{
border:none !important;
background:none !important;}
	
div.deleteform.desktop,div.navLinks{
width:90px !important;
text-align:center !important;
opacity:0.2;
position:fixed !important;
bottom:58px !important;
right:2% !important;
float:left !important;}

div#qp.reply.dialog.post{
box-shadow:0 0 10px rgba(0,150,255,0.7) !important;
padding:4px !important;
border-radius:4px;
border:1px solid #777 !important;}

div#qr.reply.dialog{
background:#333 !important;
width:260px;
position:fixed !important;
display:block !important;
left:0px !important;
right:auto !important;
top:260px !important;
cursor:default;
opacity:0.5;}

div#qr.reply.dialog:hover{
opacity:1;}

div.navLinks{
right:3% !important;
width:50px !important;
height:50px !important;
top:31px !important;}	

div.deleteform.desktop:hover,div.navLinks:hover{
opacity:1;}

div#updater.reply.dialog{
background:none !important;
border-radius:5px !important;}

div#watchedThreads_holder,div#thread_toolbox.reply,div#updater.reply.dialog:hover,div#options.reply.dialog{
text-align:left !important;
background:rgba(0,0,0,0.7) !important;
border:solid 1px #aaa !important;
box-shadow:0 0 10px rgba(0,160,255,0.5);}

div#options.reply.dialog{
border-radius:5px;}

span.subject{
color:#55aaff !important;}

span.abbr{
color:#eee !important;}

.quotelink,a.quotelink,a,a:visited{
color:#006cfe !important;}

a:hover{
text-shadow:1px 1px 5px #00deff, 1px 1px #00deff;}

a.replylink,div#absbot a{color:#34345C!important;
text-decoration:underline!important;}

img.title{
width:250px;
margin:0 auto !important;}

div#watcher{
position:fixed !important;
float:left !important;
background:#222 !important;
border-radius:8px;
border:1px solid #ccc !important;
padding:4px !important;
opacity:0.5;}

div#watcher:hover{
opacity:1;}


div.boardTitle{
font-size:20px !important;}

div.boardBanner{
width:auto !important;
max-width:250px;
text-align:center;
top:35px;
position:fixed;
clear:both !important;
float:left !important;
color:#0a5faf !important;
opacity:0.5;}

div.boardBanner:hover{
opacity:1;}


div#boardNavDesktop{
max-width:100%;
border-bottom:1px solid #666;
border-radius:0 0 10px 10px !important;
text-align:center !important;
background:#000 !important;
font-size:9pt;
color:#34345C !important;
position:fixed;
top:0px;
opacity: 0.5;
z-index:5;}

div#boardNavDesktop:hover{
opacity: 1;}

div#boardNavDesktop a{
font-weight:normal;
padding:1px;
text-decoration:none;
color:#19a9ec !important;}

div.board{
display:table-cell !important;
padding:0 !important;
width:88% !important;
margin-bottom:30px !important;}

form#delform:after{
clear: both !important;
content: "";
display: block !important;}

form#delform{
display:table !important;
margin-top:35px !important;
margin-left:260px !important;
width:auto !important;
height:auto !important;
float:left !important;}

div.postContainer.replyContainer,div.post.reply{
margin:0px !important;}

div.post.op{
height:auto !important;
display:block !important;}

div.post.reply{
display:block !important;
border:none !important;
border-bottom:1px solid #444 !important;
background:#222 !important;}

div.post.reply:hover{
background:#333 !important;}

div.pagelist strong{
color:#0f4690 !important;}

div.thread{
box-shadow:0 0 20px rgba(0,0,0,0.5);
border-radius:10px;
border:1px solid #555;
background:#111;
padding-bottom:8px !important;
margin-bottom:20px !important;
box-shadow:0 0 5px rgba(0,0,0,1);}


div.thread:hover{
background:#161616;}

div.pagelist.desktop{
color:#555 !important;
background:none !important;
position:fixed;
top:270px;
right:-5.5%;
-moz-transform:rotate(90deg);
-webkit-transform:rotate(90deg);
-o-transform:rotate(90deg);
border:none !important;}

.sideArrows{
color:#0077ff !important;}

h1{
position:fixed !important;
float:left !important;
left:50px;
top:180px;}

div.captcha img{
width:260px !important;}

div.move{
cursor:default !important;
margin-bottom:5px;}

div.move span a{
position:relative;
left: -40px !important;}

div.move span select{
position:relative;
left: -48px !important;}

.quote{
color:#4e84dd !important;}

span.name{
color:#0066cc !important;}

span.postertrip{
color:#003366 !important;}

span.fileText,span.summary,desktop{
margin-left:5px !important;}

a.fileThumb{
position:relative !important;
clear:both !important;}

/*FORMS*/

table#postForm.postForm.hideMobile input[type='file'],table#postForm.postForm.hideMobile input[type='text'],table#postForm.postForm.hideMobile textarea{
margin-left:4px !important;
height:20px !important;
width:250px !important;
padding:5px 8px !important;}

div.deleteform.desktop input[type='password'],div.deleteform.desktop input[type='submit'],div.deleteform.desktop input[type='button']{
margin-top:2px;
padding:5px 20px !important;
border-radius: 3px !important;}

div.deleteform.desktop input[type='password']{
padding:5px 6px !important;}

input.field,input[type='file'],input[type='text'],textarea,input[type='password']{
-moz-appearance: none !important;
-webkit-appearance: none !important;
-o-appearance: none !important;
background:#444;
border-radius: 3px !important;
border: 1px solid #666 !important;
box-shadow:0 0 1px 1px rgba(0,0,0,1) inset, 0px 20px 5px rgba(0,0,0,0.1) inset !important;}

input.field{
border-radius: 0 !important;}

table#postForm.postForm.hideMobile input[type='file']{
margin-left:-9px !important;}

input.field,input[type='file']:hover, input[type='text']:hover,textarea:hover,input[type='password']:hover{
box-shadow: 0px 8px 5px rgba(0,0,0,0.1) inset, 0px 20px 5px rgba(0,0,0,0.1) inset  !important;}

input.field,input[type='file']:focus, input[type='text']:focus,textarea:focus,input[type='password']:focus{
box-shadow: 2px 2px 5px rgba(0,0,0,0.7) inset, 0px 20px 5px rgba(0,0,0,0.1) inset  !important;}
	
input[type='file'],textarea,input[type='password']{
height:auto !important;
width:auto !important;}

input[type='password']{
margin-left:0px !important;
width:75px !important;}

td input[type='submit'], td input[type='button']{
margin:4px !important;
padding: 3px 20px !important;}


table#postForm.postForm.hideMobile input[type='submit'],table#postForm.postForm.hideMobile input[type='button']{
padding: 4px 20px !important;
margin-top:3px !important;}

input[type='submit'],input[type='button']{
-moz-appearance: none !important;
-moz-user-select: none !important;
-webkit-appearance: none !important;
-webkit-user-select: none !important;
-o-appearance: none !important;
-o-user-select: none !important;
background-image: -webkit-linear-gradient(top, #333, #111) !important;
background-image: -o-linear-gradient(top, #333, #111) !important;
background-image: -moz-linear-gradient(top, #333, #111) !important;
border: 1px solid #002 !important;
border-radius: 2px !important;
box-shadow:0 1px 1px #666 inset,0 1px 0px #333 !important;
color: #ddd !important;
text-align: center !important;
text-shadow: 0 -1px 0 #636363, 0 1px 0 #4e4e4e !important;}
    
input[type='submit']:hover,input[type='button']:hover{
color: #eee !important;
background-image: -moz-linear-gradient(top, #555, #222) !important;
background-image: -webkit-linear-gradient(top, #555, #222) !important;
background-image: -o-linear-gradient(top, #555, #222) !important;
border-color:#003 !important;
box-shadow:0 1px 1px #777 inset,0 1px 0px #444 !important;
text-shadow: 0 -1px 0 #737373, 0 1px 0 #5e5e5e !important;}    
    
input[type='submit']:active,input[type='button']:active{
color: #ccc !important;
background-image: -moz-linear-gradient(top, #222, #000) !important;
background-image: -webkit-linear-gradient(top, #222, #000) !important;
background-image: -o-linear-gradient(top, #222, #000) !important;
border-color:#001 !important;
box-shadow:0 1px 1px #555 inset,0 1px 0px #222 !important;
text-shadow: 0 -1px 0 #535353, 0 1px 0 #3e3e3e !important;}
}
