@-moz-document
domain("ogame.it"){

/* AntiGame */
.antires{background: none !important;padding:0!important;padding-top:10px!important; background-image:url('http://i49.tinypic.com/j8nmv4.png')!important;border: 0 !important;width: 153px !important; height: 63px !important}
#antires_cont{ background-image:url('http://i47.tinypic.com/b4b6h5.png')!important;padding-top:25px!important;border: 0 !important;left: -160px !important; width: 150px !important; height: 250px !important}
#antires_cont .antires{background-image:url('http://i47.tinypic.com/2007l3o.png')!important; width: 150px !important; height: 43px !important;margin-top:5px!important}
.anti_item_name{margin-top:8px!important; margin-left:3px!important; text-align:center !important; width: auto!important}
.anti_item_name + .anti_item_name{display:none!important}

/*Hide resource and move officers*/
#officers{position:absolute !important; left: 250px!important; width:auto!important}
#officers a{margin-right: 66px !important}
#metal_box{display:none !important}
#crystal_box{display:none !important}
#deuterium_box{display:none !important}
#energy_box{display:none !important}
#darkmatter_box{display:none !important}

/*Icon menu*/
.menu_icon img {display:none!important;margin-top:3px!important;width:25px!important;height:25px!important; margin-left:-26px!important;opacity:0.75!important}
.menu_icon a img {display:inline!important}
}
