css:
@-moz-document domain("chess.com") {

#body,body, #main, .dojochess, .dojochess, .dijitContentPane, .commentbody, #layout_top_container, .timerin, #overlay_loading_background,
ul#sitemap li ul.disc li span, ul.columns.two.page-wide li ul.disc li span, .content .user-content, .content .user-content p,
.fquote, .content ul.disc li, .content ul.disc li span, .content .dark, .content .light, ul.form label, ul.form .label,
table.default td, #notation a, #notation a:visited, .notator a, .notator a:visited, #conditionalnotation a, #conditionalnotation a:visited,
.claro .dijitTabContainerTabListNested .dijitTabChecked .tabLabel ,
h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em,ins,q, s, samp, small, strike, strong, sub, sup, tt, var, b, u, i, center
dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td, article, aside
details, figcaption, footer, header, hgroup, menu, section, summary, time,players,#players1,#players2,#dijit_layout_ContentPane_10
  {color:#cacaca !important; background-color: #000000 !important;}

  div,#seekGraph_graph,#dijit_layout_ContentPane_10,#dijit_layout_ContentPane_9
  {color:#cacaca !important; background-color: #000000 !important;}
  
  
  
.msgme,.usrme,.chatConsoleAdvanced,.claro .dojoxGridScrollbox,input:focus, textarea:focus {background-color: #000000 !important; color: #cfceaf !important;}
  
  select,input {background-color:#BABAC9 !important; }
  
.msgfriend {background-color: #000000 !important; color: #cfceaf !important;}
.dj_ie .dijitSplitterCover,.dojoxGridMasterMessages {
	background: silver !important;}

user-upgrade-message-text,th,.popUpInfoDiv,.bold, td, #main_bc, .clientContentPane, .ratings .stats-header, .clientContentPaneNoBorder, .claro .dijitTabContainerNested .dijitTabContainerTop-tabs .dijitTab .dijitTabContent, label, .label,label.bold {
  background-color:green !important;background:black !important; color:#ffffff !important;}
  
p, table.sidebar-stats td {color: #999999 !important;}

.framed.alerts h1,.framed.alerts  h2,.framed.alerts  h3,.framed.alerts  h4,.framed.alerts  h5,.framed.alerts h6,.dark,.framed,.framed label,.section-title,.claro .tabLabel  {
color:#2b910d !important;
}
.framed.blue {
background-color:black !important;
}

.framed.alerts,.framed,.button-upgrade:active, .button-upgrade:focus,.button-upgrade,.button-submit,.button-submit.xlarge,.you-play {
background-color:#4e4e4e !important;color:silver !important;
}

.button-submit:active, .button-submit:focus {
background-image: -moz-linear-gradient(0% 100% 90deg, #4e4e4e, #676767) !important;
background-color:#741a03 !important;
}

table.default td, table.default a,.pagination li .disabled, .pagination-bottom li .disabled, .pagination li.previous-off:before, .pagination-bottom li.previous-off:before, .content .light, .crumbs li, .crumbs li a, p em 
 {color:#d6d6d6 !important;
}


select:focus,.dijitTab .tabLabel  {
border-color:#31b70b !important;
}

.button-submit:active, .button-submit:focus,.button-submit,a.button-submit, a.button-admin, a.button-facebook, a.button-play, a.button-go, a.button-premium, a.button-flat.selected-yes, a.button-submit:visited, a.button-admin:visited, a.button-facebook:visited, a.button-play:visited, a.button-go:visited, a.button-premium:visited, a.button-flat.selected-yes:visited {
background-color:#99520b !important;
}


.dijitTab .tabLabel,a {
color:#31b70b !important;
}

element,.note-form .first-item input  {
background-color:#c0c0c0 !important;color:#1307eb !important;
}
ul#nav, #main_bc_header,.button-go,a.button-submit.large, a.button-go.large, a.button-cancel.large, a.button-cancel-dark.large, a.button-admin.large, a.button-play.large, a.button-premium.large, a.button-flat.large {
background-color:#515151 !important;color:#8eb689 !important;
}
table.sidebar-stats th, table.sidebar-stats td {
background-color:#bcdbfe !important;
}
.dijitTab, .tabLabel,b, tbody,strong,.dojoxGridRow,.dojoxGridCell,.cellLeft,.cellBold,.cellPointer,.dojoxGridRowTable {color:#ffffff !important;}
}
