@-moz-document url("https://pastee.org/"), url("https://pastee.org/api"), url("https://pastee.org/statistics"), url("https://pastee.org/about") {
div#container {
    margin-left: auto !important;
    margin-right: auto !important;
    width: 100% !important;
    padding-top: 64px !important;
    overflow-x: hidden;
}

div#footer {
    display: none;
}

div#paste {
    word-wrap: break-word;
    overflow-x: scroll;
}

textarea {
    padding-left: 19px;
    padding-right: 25px;
    width: 98% !important;
    
    margin-left: auto !important;
    margin-right: auto !important;
}

div#header {
    top: 0;
    padding-bottom: 15px !important;
    width: 100%;
    position: fixed !important;
    z-index: 99;
    border-bottom: #c6c6c6 solid 1px;
    background: rgb(248,248,248);
    background: -moz-linear-gradient(top,  rgba(248,248,248,1) 0%, rgba(241,241,241,1) 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(248,248,248,1)), color-stop(100%,rgba(241,241,241,1)));
    background: -webkit-linear-gradient(top,  rgba(248,248,248,1) 0%,rgba(241,241,241,1) 100%);
    background: -o-linear-gradient(top,  rgba(248,248,248,1) 0%,rgba(241,241,241,1) 100%);
    background: -ms-linear-gradient(top,  rgba(248,248,248,1) 0%,rgba(241,241,241,1) 100%);
    background: linear-gradient(top,  rgba(248,248,248,1) 0%,rgba(241,241,241,1) 100%);
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#f8f8f8', endColorstr='#f1f1f1',GradientType=0 );
    box-shadow: 0 1px 0 rgba(255, 255, 255, 0.4),0 0 10px rgba(0, 0, 0, 0.1);

}

div#header h1, div#header h2, div#navigation {
    z-index: 100;
}

div#header h1 a {
    padding-left: 19px;
    color: #0083ff !important;
    font-size: 16pt !important;
}

div#content {
    padding-left: 29px;
    padding-right: 45px;
}

div#content h1 {
    display: none;
}

div#content h1, div#extra h1, div#sidebar h1, a {
    color: #0083ff !important;
}
div#navigation {
    right: 0;
    background: transparent !important;
    top: 0;
    -webkit-transition: .1s; 
    position: fixed !important;
    z-index: 100;
    padding-top: 8px;
    padding-right: 10px;
    color: #333 !important;
    text-shadow: 1px 1px 0 white;
    font-size: 15px !important;
    font-weight: bold !important;
}

div#navigation a {
    background: transparent;
    color: #333 !important;
    text-shadow: 1px 1px 0 white;
    font-size: 13px !important;
    font-weight: bold !important;
    padding: 6px !important;
    -webkit-transition: .1s; 
}

div#navigation a:active {
    text-shadow: -1px -1px 0 rgba(0, 0, 0, 0.3) !important;
    color: #fff !important; 
    border-radius: 4px;
    background: rgb(38,131,255);
background: -moz-linear-gradient(top,  rgba(38,131,255,1) 0%, rgba(0,110,255,1) 100%);
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(38,131,255,1)), color-stop(100%,rgba(0,110,255,1)));
background: -webkit-linear-gradient(top,  rgba(38,131,255,1) 0%,rgba(0,110,255,1) 100%);
background: -o-linear-gradient(top,  rgba(38,131,255,1) 0%,rgba(0,110,255,1) 100%);
background: -ms-linear-gradient(top,  rgba(38,131,255,1) 0%,rgba(0,110,255,1) 100%);
background: linear-gradient(top,  rgba(38,131,255,1) 0%,rgba(0,110,255,1) 100%);
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#2683ff', endColorstr='#006eff',GradientType=0 );
    -webkit-transition: .1s; 
}1

body {
    top: 5px;
}
}

