css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.netvibes.com"),domain("netvibes.com") {

.entry-footer-firstLine {
width:400px !important;
}

.entry-footer-firstLine span {
    float: left !important;
}

#smartreader-feeds-header {
 background-color:#666;
 padding-right:4px !important;
}
#smartreader-feeds-selector, #smartreader-feeds-selectionActions {
margin:0 4px 0 0 !important;
}

.header-button.toplevel, .header-dropdown.toplevel {
margin-left:4px !important;
}

.entry-footer-firstLine {
 margin:5px !important;
 width:320px;
}

.smartreader-footer .header-button.toplevel, .smartreader-footer .header-button-set {
 margin-top: 5px !important;
}

.smartreader-footer .entry-footer {
 margin:0 0 0 4px  !important;
}

.smartreader-footer {
 width:640px !important;
 position: absolute;
 top:3px;
 right:100px;
}

.entry-footer.websiteview.read {
 width:340px !important;
}

.entry-footer-firstLine {
 width:330px !important;
 display: inline-block !important;
}

.entry-footer-firstLine span strong {
 width:180px !important;
}

#smartreader-feeds-header-prevNext {
margin:5px 0 0 0 !important;
}

.smartreader-footer .entry-footer .nv-facebookLike {
display:none;
}

}
