display: block;







font-size: 13px;







margin-bottom: 25px;







padding: 15px 22px;







}















form.dashboard_options_form .option_container {







border-top: 1px solid #000000;







}















.no_posts_found, h1.dashboard_header {







color: #333333;







}















.answer_container {







background-color: #000000;







border-bottom: 0px solid #36244e;







}















#nav .nav_item .nav_menu {







