css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(audible.com) 
{
    @-moz-document regexp(".*/wl.*")
    {
        table { border-spacing: 0px !important; }
        td { padding: 0 !important; }
        .adbl-prod-image-sample-cont a img {
            margin-bottom: -4px !important;
            height: 45px !important;
            width: 45px !important;
        }
        .adbl-col-2 { width: 60px !important; }
        .adbl-col-4 .adbl-series-margin-bottom { margin-bottom: 0px !important; }
        .adbl-col-3, th:nth-child(3), /*date added */
        .adbl-col-6, th:nth-child(6), /*type*/
        .adbl-col-7, th:nth-child(7) /*price*/ { display: none !important; }
        
        /* hide page controls/info */
        .adbl-page-index, .adbl-pagination-bottom { display: none; }
    }
}
