css:
@-moz-document url-prefix("https://accounts.google.com/ServiceLogin") 
{
body
{
background-image:url('http://i.imgur.com/seqMR.jpg');
background-repeat:no-repeat;
background-attachment:fixed;
background-position:center; 
}

.google-header-bar,
.sign-in ~ *,
.google-footer-bar {display: none !important;}

.wrapper {
width: 100% !important;
height: 100% !important; 
display: table !important; 
}

#link-forgot-passwd
{ margin-left: 0px !important;
color: #000 }

.remember
{margin-left: 365px !important; }

#signIn
{  
padding: 2px 45px 0px 38px !important; 
background: #ed1c24 !important;
color: #fff !important;  
border: none !important;
font-family: Verdana !important; }

#signIn:hover
{ color: #000 !important; !important; !important; }

#Email, #Passwd
{ Border: 1px solid #ed1c24 !important;}

#Email:active, #Email:focus,
#Passwd:active, #Passwd:focus
{ 
color: #666666 !important; 
box-shadow: inset rgba(159,33,39,0.2) 0px 2px 5px 2px !important; }

#Email:hover, #Passwd:hover
{ color: #666666 !important; 
box-shadow: inset rgba(138,138,138,0.4) 0px 2px 5px 2px !important; }

.main.content {

width: 100% !important;
display:table-cell !important;
vertical-align: middle !important;
min-width: 0 !important;
max-width: none !important;
padding: 0 !important;
}

.sign-in {
Border: 4px solid rgba(138,138,138,0.4) !important;
float: none !important; 
margin: 0 auto !important;
width: 769px !important;
min-width: 450px !important;
}

.signin-box {margin: 0 !important;}
}
