css:
@-moz-document url-prefix("https://www.google.com/accounts/"), url-prefix("https://accounts.google.com/"), url-prefix("https://www.google.com/accounts/ServiceLogin?"), url-prefix("https://accounts.google.com/ServiceLogin?") {
.logo, .signin-box h2, .product-info.youtube, .product-info.mail, .signup-button, .features li img, .product-info, .google-footer-bar ul
{ display: none !important; }

.google-header-bar, .signin-box, .google-footer-bar
{ background: transparent !important;
border: none !important; }

div, td, .button, #PersistentCookieLabel, #link-signup
{ color: black !important; }

.features li
{ margin-left: -68px !important; }

.sign-in
{
width: 100%
vertical-align: middle !important;
position: fixed;
  margin-left: -351px;
  top: 35%; 
  left: 50%;
background: rgba(10,10,10, 0.1) !important; 
box-shadow: inset rgba(0,0,0, .4) 0px 8px 0px 12px, 0px 0px 0px 0px rgba(0,0,0, .1) !important;
border: 1px solid rgba(0, 0, 0, .2);
-webkit-background-clip: padding-box; /* for Safari */
background-clip: padding-box; /* for IE9+, Firefox 4+, Opera, Chrome */!important; 
width: 700px !important;
}

.gaia.le.button
{ margin-left: 80px !important; }

#link-forgot-passwd
{ margin-left: 0px !important;
color: #000 }

.remember
{ margin-left: 274px !important; } 

#signIn
{  
padding: 2px 45px 0px 38px !important; 
background: #ed1c24 !important;
color: #fff !important;  
border: none !important;
font-family: Verdana !important; }

#signIn:hover
{ color: #000 !important; !important; !important; }

strong
{ color: black !important; }

#Email, #Passwd, #PersistentCookie, .lang-chooser, .lang-chooser option
{ background: transparent !important; 
box-shadow: inset rgba(0,0,0, .4) 0px 2px 4px 2px !important;
border-top-color: #111 !important;
border-right-color: #333 !important;
border-bottom-color: #444 !important;
border-left-color: #333 !important;
border-radius: 3px !important; }

#Email:active, #Email:focus, #Email:hover,
#Passwd:active, #Passwd:focus, #Passwd:hover
{ -moz-appearance: none !important; 
color: #666666 !important; 
background:  transparent) !important;
box-shadow: inset rgba(98,128,136,0.5) 0px 2px 12px 2px !important; }


#PersistentCookie
{ color: #ab0000 !important; }

strong 
{ color: #000 !important;
text-shadow: 0px 0px 6px rgba(185,203,194,0.4) !important; 
font-weight: bold !important; }

INPUT[type="checkbox"]
{ -moz-appearance: none !important; 
width: 11px !important;
height: 11px !important; }

option:hover
{ -moz-appearance: none !important; 
color: #646D4A !important;
text-shadow: 1px 2px 1px #000 !important;  
background: -moz-radial-gradient(center top, #A6AD92, transparent),-moz-linear-gradient(top, rgba(0,0,8, .2), #555) !important;
box-shadow: inset rgba(98,128,136,0.5) 0px 2px 12px 2px !important; }

html
{
background-image:url('http://i.imgur.com/seqMR.jpg');
background-repeat:no-repeat;
background-attachment:fixed;
background-position:center; 
}

}
