@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("t411.me") {
body { margin:0px !important; }
.block { margin:0 !important; padding: 0 !important; }
.content { margin-left:200px !important; margin-right:0 !important; }
.block h2 { border:none !important; box-shadow:none !important; margin:0 !important; }
.forumPosts { display:none !important; }
#progress { display:none !important; }
#flashTags { display:none !important; }
.itemWrapperHalf.alignleft { display:none !important; }
.block > h2 { -webkit-border-radius:0 !important; text-indent:0 !important; }
.block > h2 span { -webkit-border-radius:0 !important; }
.ads { display:none !important; }
.details iframe { display:none !important; }
form#comment-form { display:none !important; }
.share { display:none !important; }
#lavamenu { margin-top:21px !important; }
#lavamenu ul { border:1px solid royalblue !important; background:#fff !important; color:black !important; -webkit-border-radius:0 !important; }
#lavamenu ul li { border-bottom: none; }
#lavamenu ul a { color:black !important; }
#lavamenu ul a:hover { text-decoration:underline !important; }
#lavamenu > li { text-align:left !important; }
#right, #left, .banner728, .backLava { display:none !important; }

html body div.headerPlace div.headWrapper div.head ul#lavamenu li ul {
    width:260px !important;
    min-width:260px !important;
    opacity:1 !important;
    padding:0px !important;
    left:0 !important;
    }
html body div.headerPlace div.headWrapper div.head ul#lavamenu li:nth-child(3) ul li { 
    padding:2px !important;
    }
html body div.headerPlace div.headWrapper div.head ul#lavamenu li:nth-child(3) ul {
    height:220px !important;
    display:block !important; width:200px !important;min-width:190px !important;
    position:fixed !important;
    left:0px !important;
    top:83px !important;
    }
.selectedLava { background:none !important; border:none !important; }
.logo { display:none; }
.logos { display:none; }
.headWrapper { height:55px !important; box-shadow:none !important; }

table.results tr, table.results td { padding:0 !important; }
#browseform { position:fixed; right:0px; width:200px !important; top:85px; overflow-y:scroll !important; height:100%; }
#browseform span { top:-1px !important }
#browseform label { padding:0 !important }
#browseform li { margin:0 !important }

html body div.wrapper div.contentWrapper div.content div.block h2 { text-indent:100px; }
html body div.wrapper div.contentWrapper div.content div.block div.details table tbody tr td a.btn:nth-child(1) {
    position:fixed;
    top:150px;
    left:210px;
    color: black !important;
    background: yellow !important;
    padding: 10px;
    border: solid 1px black;
    -webkit-border-radius:5px !important;
    font-size: 14px;
    }
.userBox {
    position:fixed;
    top:320px;
    left:0px;
    height:auto;
    width:200px;
    padding:0;
    margin:0;
    border:1px solid royalblue;
    -webkit-border-radius:0px;
    }
.userBox .column { float:none; }
.userBox .avatar-big { display:none; }
.userBox li { padding:1px 2px;}
.userBox .column a { color:black !important; font-size:14px !important; line-height:18px !important; }
.loginBar .slideToo { display:none; }
}

