/* Main Style */

/*---- Header Bar ----*/

.google-header-bar {
background: rgba(236, 240, 241, 0.9) !important;
border-bottom: none !important;
}

/*---- Login Screen Genaral Styles ----*/

input[type=email], input[type=password] {
-moz-appearance: none;
-webkit-appearance: none;
appearance: none;
display: inline-block;
height: 36px;
padding: 0 8px;
margin: 1px 0 5px !important;
background: #fff;
border-top: none !important;
-moz-border-radius: 0 !important;
-webkit-border-radius: 0 !important;
color: #404040;
-webkit-box-sizing: border-box !important;
box-sizing: border-box !important;
border: 1px solid #eee !important;
border-radius: 0 !important;
font-family: "Open Sans", sans-serif !important;
font-weight: 300 !important;
font-size: 16px !important;
/* text-transform: uppercase !important; */
-webkit-box-shadow: none !important;
-moz-box-shadow: none !important;
box-shadow: none !important;
}

input[type=email]:focus, input[type=password]:focus {
border: 1px solid #3079ed !important;
}

/* Hide Big promo Heading */

.banner h1 {
color: transparent !important;
}

/*---- Style smaller Heading Google Services Indication ----*/

.banner h2 {
color: #34495e !important;
}

/* Card Login Box */

.card {
background-color: rgba(236, 240, 241, 0.8) !important;
-moz-border-radius: 2px !important;
-webkit-border-radius: 2px !important;
border-radius: 2px !important;
-webkit-box-shadow: none !important;
-moz-box-shadow: none !important;
box-shadow: none !important;
}

/* Submit Button */

.rc-button-submit {
border: none !important;
font-weight: 300 !important;
text-shadow: none !important;
}

.rc-button {
-moz-border-radius: 0px !important;
-webkit-border-radius: 0px !important;
border-radius: 0px !important;
box-shadow: none !important;
}

/* Checkbox, Radio inputs */

input[type=checkbox], input[type=radio] {
-webkit-appearance: none;
display: inline-block;
width: 14px !important;
height: 14px !important;
background: #fff;
border: 1px solid #bdc3c7 !important;
-moz-border-radius: 10px !important;
-webkit-border-radius: 10px !important;
border-radius: 10px !important;
box-shadow: none !important;
}


/* Bubble, Help Links, Password Recovery */

.remember {
color: #95a5a6 !important;
}

.bubble {
color: #34495e !important;
}

#link-forgot-passwd {
color: #bdc3c7 !important;
}

/* Create account - Sign Up Link */

#link-signup {
color: #bdc3c7 !important;
background-color: #ecf0f1 !important;
font-size: 12px !important;
padding: 6px !important;
cursor: pointer !important;
text-align: center !important;
text-decoration: none !important;
text-transform: uppercase !important;
border-radius: 2px !important;
-webkit-border-radius: 2px !important;
-moz-border-radius: 2px !important;
}

#link-signup:hover {
color: #fff !important;
background-color: #357ae8 !important;
}

/* One Google Apps Image */

.one-google img {
display: block;
width: 0 !important;
height: 0 !important;
margin: 10px auto;
padding-left: 220px !important;
padding-top: 17px !important;
background-image: url('https://dl.dropboxusercontent.com/u/12675809/logo_strip_flat-color_2x.png') !important;
background-size: 220px 17px !important;
}

.one-google p {
color: #bdc3c7 !important;
}

.google-footer-bar {
border-top: none !important;
}

.footer a {
color: #95a5a6 !important;
}

.footer ul li {
color: #95a5a6 !important;
}

