/* This userstyle is mostly maintained with Firefox. I don't regularly use Chromide and therefore I'm mostly unaware/uninterested with any bugs with the script for Chrome users.

ammy */
@-moz-document domain("roblox.com") {
#ctl00_cphBanner_SystemAlertTextColor {
background-color: #D6F0DA !important;
color: black;
}
.CenterColumn a:visited {
color: #222222;
}
a.menuTextLink:visited {
font-size: 0.9em;
text-decoration: none;
color: #222222;
}
.CenterColumn a {
color: black;
}
a.menuTextLink {
font-size: 0.9em;
text-decoration: none;
color: black;
}
#AuthenticationBannerSpan
{
border-radius: 4px !important;
background-image:None !important;
background-color:#D6F0DA !important;
}
.UserBlurb {
overflow: hidden;
width: 348px;
padding: 5px 5px 5px 5px;
line-height: 20px;
background-color: #EEFFF7;
}

#MasterContainer
{
	background:#EEFFF2 !important;
}

th
{
background:#444444 !important;
}

#BodyWrapper {
background: #EEFFF2 !important;
background-color: #EEFFF2 !important;
}

body {
background:#EEFFF2 !important; 
}
#AdvertisingLeaderboard
{
display:none !important;
}
.forumRowHighlight
{
background:#D6F0DA !important;
}
.forumHeaderBackgroundAlternate
{
background:#D6F0DA !important;
}
.tableHeaderText
{
background:#444444 !important;
padding: 0px !important;
}
.forumAlternate
{
background:#D6F0DA !important;
}
.forumRow
{
background:#D6F0DA !important;
}

.tableBorder
{
border: 1px black solid !important;
background:#CCE1D2 !important;
box-shadow: 0pt 1px 10px rgba(0, 0, 0, 0.4) !important; /*Shadows! S-so cool ;_;*/
}
.GridHeader
{
Background:#777777 !important;
}
.Navigation li:hover
{
background:#444444 !important;
}
#SmallHeaderContainer #Banner {
background-color: #444444;
}
a.btn-logo, a.btn-logo:visited {
background: rgb(25, 25, 25) !important;
display: inline-block;
width: 123px;
height: 38px;
float: left;
}
#HeaderContainer {
	    background: #444444 !important;
	    height: 1px !important;
	    margin: 0 !important;
	    padding: 0 !important;
	    }
Header .Navigation {
width: 900px;
height: 30px;
background: url(http://www.roblox.com/images/cssspecific/rbx2/topNav_blue.png);
}
#Body
{
background:#EEFFF2 !important;
}
.StandardBox
{
background:#EEFFF2 !important;
}
.Navigation
{
background:#444444 !important;
border-top:1px dashed #ffffff;
}
.Repeater .AlternatingItemTemplateEven {
background: #EEFFF2;
padding: 5px 5px 5px 5px;
}
#Footer {
display:none !important;
}
.ParentsSection
{
border:2px ridge #ffffff !important;
background:#333333 !important;
color:#ffffff !important;
}
.GridPager
{
background:#777777 !important;
}
.page
{
background-image:None !important;
background:#EEFFF2 !important;
}
.DescriptionText
{
border:2px ridge #ffffff !important;
background:#333333 !important;
color:#FFFFFF !important;
}
div#Banner.BannerRedesign {
background: none !important;
background-color: rgb(25, 25, 25) !important;
height: 36px !important;
position: static !important;
}
div.mySubmenuFixed.Redesign, .subMenu {
position: static !important;
}
div.SystemAlert, div.SystemAlertText /*No more of that obnoxious
test.roblox.com advertising! This will also make NoScript users like myself
very happy. */
{
display:none;
}
div.forceSpaceUnderSubmenu, div.forceSpace
/*Zero tolerance for wasted
screen space!*/ {
display:none !important;
}
div.HeaderDivider /*Looks unnatural with the rest of the dark appearance */
{
display:none;
}


}
