css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.strike-the-root.com/") {

A:link, a:link span, a:link font { color: #ff0000 !important; text-decoration: underline !important; background-color:inherit !important; }
A:visited, a:visited span, a:visited font { color: #990000 !important; text-decoration: underline !important; background-color:inherit !important; }
A:active, a:active span, a:active font { color: #ff0000 !important; text-decoration: underline !important; background-color:inherit !important; }
A:hover, a:hover span, a:hover font { color: #ff0000 !important; text-decoration: underline !important; background-color:inherit !important; }

BODY, #content { width: 99% !important; }

.views-row { width:80% !important;
  border: 1px solid black;
  -moz-border-radius: 10px;
  color: black;
  background-color: #ffffff;
  margin: 10px 0px 10px 0px;
  padding: 8px;
  border-width: 1px;
  border-color: #c0c0c0 #606060 #606060 #c0c0c0;
  position: relative; 
  text-align: justify; }

}
