    background: -webkit-linear-gradient(-90deg, rgba(58, 58, 58, 0.8), rgba(0, 0, 0, 1.0), rgba(29, 29, 29, 0.8)) !important;
    background-size: 100% 100% !important;
    position: absolute !important;
    top: 525px !important;
    right: 9px !important;
    border-radius: 0px 0px 20px 20px !important;
    z-index: 9999 !important;
}

#expeditionbutton .text,
#showbutton .text,
#expeditionbutton .bright,
#showbutton .bright,
#expeditionbutton .bleft,
#showbutton .bleft {
    position: relative !important;
    left: 228px !important;
}

/*************SELECTEUR********************/
a[class="backGalaxy"] {
    background: url("https://www.g-francois.fr/images/skin/direct1.gif") no-repeat scroll 0px -24px rgba(0, 0, 0, 0)!important;
}

a[class="backGalaxy"]:hover {
    background: url("https://www.g-francois.fr/images/skin/direct1.gif") no-repeat scroll -49px -24px rgba(0, 0, 0, 0)!important;
    box-shadow: 0px 0px 5px 4px #8accff !important;
    -moz-transition: box-shadow 0.8s ease !important;
    -webkit-transition: box-shadow 0.8s ease !important;
    border-radius: 20px 4px 4px 20px !important;
}

a[class="forwardGalaxy"] {
    background: url("https://www.g-francois.fr/images/skin/direct1.gif") no-repeat scroll 0px 0px rgba(0, 0, 0, 0)!important;
}

a[class="forwardGalaxy"]:hover {
    background: url("https://www.g-francois.fr/images/skin/direct1.gif") no-repeat scroll -49px 0px rgba(0, 0, 0, 0)!important;
    box-shadow: 0px 0px 5px 4px #8accff !important;
    -moz-transition: box-shadow 0.8s ease !important;
    -webkit-transition: box-shadow 0.8s ease !important;
    border-radius: 4px 20px 20px 4px !important;
}

#expeditionbutton .bright,
#showbutton .bright {
    background: url("https://www.g-francois.fr/images/skin/direct1.gif") no-repeat scroll 0px 0px rgba(0, 0, 0, 0)!important;
    width: 19px !important;
}

#expeditionbutton .bright:hover,
#showbutton .bright:hover {
    background: url("https://www.g-francois.fr/images/skin/direct1.gif") no-repeat scroll -25px 0px rgba(0, 0, 0, 0)!important;
    width: 19px !important;
}

#expeditionbutton .bright:hover,
#showbutton .bright:hover {
    border-radius: 4px 20px 20px 4px !important;
    box-shadow: 0px 0px 5px 4px #8accff !important;
    -moz-transition: box-shadow 0.8s ease !important;
    -webkit-transition: box-shadow 0.8s ease !important;
}


#expeditionbutton .text,
#showbutton .text {
    position: relative !important;
    left: 224px !important;
    border-radius: 4px 4px 4px 4px !important;
}


#expeditionbutton .text:hover,
#showbutton .text:hover,
#expeditionbutton .bleft:hover,
#showbutton .bleft:hover {
    box-shadow: 0px 0px 5px 4px #8accff !important;
    -moz-transition: box-shadow 0.8s ease !important;
    -webkit-transition: box-shadow 0.8s ease !important;
}

/*************MARCHAND******************/
#traderOverview .div_trader .header {
    background: none !important;
}

#traderOverview .div_trader .content {
    background: none !important;
    background: linear-gradient(to bottom, rgba(0, 0, 0, 0.8), rgba(255, 255, 255, 0.8), rgba(0, 0, 0, 0.8)) !important;
    border-radius: 20px 20px 10px 10px !important;
    background-size: 100% 100% !important;
    border: none !important;
}

#traderOverview .div_trader .footer {
    background-size: 100% 30% !important;
    background-position: center top !important;
    background-repeat: no-repeat !important;
}
/*************/
#traderOverview .div_trader .left_box .left_header,
#traderOverview .div_trader .right_box .right_header {
    background: none !important;
    background-size: 100% 60% !important;
    background-position: 0px 14px !important;
    background-repeat: no-repeat !important;
}

#traderOverview .div_trader .left_box .left_content {
    background: none !important;
    background: linear-gradient(to top, rgba(0, 0, 0, 0.9), rgba(0, 0, 0, 0.8), rgba(38, 38, 38, 0.9)) !important;
    border-radius: 20px 20px 0px 0px !important;
}

#traderOverview .div_trader .left_box .left_footer {

    background: none !important;
    background: linear-gradient(to bottom, rgba(0, 0, 0, 1.0), rgba(0, 0, 0, 1.0), rgba(0, 0, 0, 0.2)) !important;
    border-radius: 0px 0px 10px 10px !important;
}

.left_header > h2:nth-child(1),
.right_header > h2:nth-child(1) {
    position: relative !important;
    top: 6px !important;
}
/**************/
#traderOverview .div_trader .right_box .right_content {
    background: none !important;
    background: linear-gradient(to top, rgba(0, 0, 0, 0.9), rgba(0, 0, 0, 0.8), rgba(38, 38, 38, 0.9)) !important;
    border-radius: 20px 20px 10px 10px !important;
}
#traderOverview .bargain_overlay,
#traderOverview .div_trader .right_box .right_footer {
    background: none !important;
}
/****/
#traderOverview .currentPlanet,
#traderOverview .toggleLink,
#traderOverview .togglePanel {
    background: none !important;
    border: 1px solid #4d4d4d !important;
    color: #ffffff !important;
    background: linear-gradient(to left, rgba(36, 36, 36, 1.0), rgba(88, 88, 88, 1.0), rgba(38, 38, 38, 1.0)) !important;
}
/**********OPTION*************/
#preferences .contentzs .tabwrapper,
#happyedit .contentzs .tabwrapper {
    background: none !important;
    background: linear-gradient(to bottom, #3b3b3b 0%, #101419 80%) !important;
    border-radius: 20px 20px 0px 0px !important;
}
#preferences #content,
#happyedit #content {
    background: none !important;
    background: linear-gradient(to bottom, #3b3b3b 0%, #101419 80%) !important;
    border-radius: 20px 20px 0px 0px !important;
}

