css:
/*
This script is intended to make navigating the cases in tribunal 
faster and more intuitive with a more condensed interface.
*/


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://euw.leagueoflegends.com/tribunal/"),
url-prefix("http://eune.leagueoflegends.com/tribunal/"),
url-prefix("http://br.leagueoflegends.com/tribunal/"),
url-prefix("http://na.leagueoflegends.com/tribunal/")
{

/* Global change to gain a little space on top*/ 
#tribunal-case .title-container, #tribunal-case h3 {
    display: none !important;
    }

#tribunal_content_wrapper .header {
    margin-bottom: 0px !important;
    padding: 5px 0 !important;
    margin-top: -10px !important;
    }
#tribunal_content_wrapper .faq {
    top: 6px !important;
    }
#tribunal-case .tab-box {
    padding: 0px !important;
    }
.game-stats-container {
    margin:0px !important;
    padding: 3px 0 !important;
    width: 12% !important;
    float:left !important;
    border: 1px solid #EBD4BA !important;
    border-radius: 7px;
    margin: 0 0 10px !important;    
    background: #FAF1D5 !important;
    }
#tribunal-case .game-stats {
    background : transparent !important;
    }
#tribunal-case .stat {
    border-right: none !important;
    }
#tribunal-case .legend {
    width : 13% !important;
    }
.game-stats>.stat {
    clear:left !important;
    width: 95% !important;
    text-align: center !important;
    padding: 2px !important;
    }
.tab-content {
    padding: 7px !important;
    }

.comments, .chat-log {
    padding: 7px !important;
    }
.comments-number {
    font-size: 14px !important;    
    height: 18px !important;
    margin-right: 3px !important;
    padding: 2px  0px 2px 0 !important;
    background-image: none !important;
    border-radius: 6px !important;
    }
.comment > .enemy {    
    background-color: #860B00 !important;
    }
.comment > .ally {    
    background-color: green !important;
    }    

.report-comments {
    padding-bottom: 3px !important;
    padding-top: 0px !important;
    font-size: 12px !important;
    }
#tribunal-case .comment {
    padding-bottom: 3px !important;
    }
#tribunal-case .comments-container {
    clear:none !important;
    width: 71% !important;
    float: left !important;
    margin: 0 0px 7px 7px !important;
    background-color: #FAF1D5;
    border: 1px solid #EBD4BA;
    border-radius: 7px 7px 7px 7px;
    }
.comments-container > h3 {
    margin:0 3px 3px !important;
    }
/* Fixed verdict box on the left*/
.voter-container:before, .verdict-container:before {
    content: " ";
    float: left;
    background: url("data:image/png;base64,") no-repeat scroll transparent;
    height: 10px;
    left: 1px;
    position: absolute;
    top: -9px;
    width: 135px;
    border-top-left-radius : 5px;
    border-top-right-radius : 5px;
    }
.voter-container:after, .verdict-container:after {
    content: " ";
    float: left;
    background: url("data:image/png;base64,") no-repeat scroll transparent;
    height: 10px;
    left: 1px;
    position: absolute;
    bottom: -9px;
    width: 135px;
    border-bottom-left-radius : 5px;
    border-bottom-right-radius : 5px;
    }
.voter-container, .verdict-container {
    float: none !important;
    position: fixed !important;
    width: 142px !important;
    height: auto !important;
    left: 50% !important;
    margin-left: -640px !important;
    top: 240px;
    background : url("data:image/png;base64,");
    }
#tribunal-case .verdict-container {
    border: none !important;
    padding: 0px !important;
    }
.voter-container>.button {
    margin: 2px !important;
    width: 130px !important;
    border-radius: 15px !important;
    height: 30px !important;
    }  
#tribunal-case .verdict-block {
    width: 90% !important;
    height: auto !important;
    padding : 10px 0px !important;
    margin: 2px !important;
    border-radius: 5px !important;
    }
.verdict-container>.pardon {
    background: url("http://na.leagueoflegends.com/tribunal/bundles/riottribunal/images/tribunal-sprite.png") no-repeat scroll -22px -497px transparent !important;
    }
.pardon>p, .punish>p {
    padding-left: 25px !important;
    }
.verdict-container>.punish {
    background: url("http://na.leagueoflegends.com/tribunal/bundles/riottribunal/images/tribunal-sprite.png") no-repeat scroll -22px -420px transparent !important;
    }
#tribunal-case .timer {
    width: 122px !important;
    float: right !important;
    margin:2px !important;
    height: auto !important;
    }
#tribunal-case .seconds {
    display: inline-block;
    min-width: 40px !important;
    clear: left !important;
    }

.voter-block {
    border: 1px solid #EBD4BA;
    margin:2px !important;
    width: 120px !important;
    float: right !important;
    padding: 3px !important;
    border-radius: 7px;
    }
#tribunal-case .skip {
    margin-right: 10px !important;
    margin-bottom: 3px !important;
    }
    
/*Minimalist champion list + double column*/

#tribunal-case .comments, #tribunal-case .chat, #tribunal-case .game-stats {
    background: none !important;
    padding: 5px 0px 0px !important;
    }

tr.reported-player, tr.ally {
    height: 72px !important;
    }
tr.alliesFilter, tr.enemiesFilter {
    height: auto !important;
    }
td.player-champion > img {
    height:50px !important;
    width:50px !important;
    }
.player-champion {
    font-size: 0px !important;
    }

#tribunal-case .players-container img {
    padding-right: 2px !important;
    padding-bottom: 2px !important;
    }
td.player-items {
    width: 150px !important;
    height: 56px !important;
    }
td.player-items > div, td.player-summoner-spells > div {
    -moz-transform:scale(0.5);
    -webkit-transform:scale(0.5);
    -o-transform:scale(0.5);
    margin: -11px !important;
    float: left !important;
    }
td.player-summoner-spells > div {
    float: top !important;
    }
td.player-summoner-spells {
    width: 30px !important;
    }
#tribunal-case .players-container td {
    padding: 2px !important;
    border-top: solid 1px #EBD4BA;
}

.players-container {
    height: 400px !important;
    width : 49% !important;
    clear: none !important;
    float: right !important;
    margin-top: 0px !important;
    }
.chat-scroll {
    height : 370px !important;
    max-height: none !important;    
    border: solid 1px #EBD4BA;
    border-radius: 5px;
    }
.chat-log-container {
    width: 50% !important;
    margin-top : 0px !important;
    }
.chat-log {
    height: 100% !important;
    font-size: 12px !important;
    border : none !important;
    }
.chat-filters {
    margin-right: 5px !important;
    height: 20px !important;
    }    
.players-container {
    float: right !important;
    width: 49.5% !important;
    clear: none !important;
    margin-top: 0px !important;
    }
    
.players-container > h3 {
    margin-left: 5px !important;
    }
#tribunal-case .chat-user, #tribunal-case .chat-timestamp {
    vertical-align:text-top;
    }
}
