@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document url("http://godvillegame.com/superhero") {

body {
overflow-x: hidden; 
color: rgb(159,159,159) !important;
background-color: #000000 !important;
}

div {
color: rgb(159,159,159) !important;
}
a:link, a:visited, .d_msg.m_infl, .fr_msg_l.m_infl {
color: rgb(73, 165, 255) !important;
}
a:hover {
color: rgb(204, 0, 0) !important;
}

#inv_block_content.inv_block ul {
padding-left: 30px !important;
}

#inv_block_content.inv_block li {
list-style-image: url('http://i1260.photobucket.com/albums/ii580/l34k3d/chest.png');
}

div.frline {
padding-top: 0px !important;
height: 10px !important;
overflow: hidden;
}

div.frline.fsearchbar {
padding-top: 0px !important;
height: 23px !important;
overflow: hidden;
}

.fr_msg_l.fr_msg_l_my {
color: rgb(17,127,250) !important;
text-decoration: none !important;
font-style: normal !important;
font-weight: bold;
font-size-adjust: 0.38;
}

.m_infl {
color: rgb(56, 89, 154) !important;
text-decoration: none !important;

}

