css:
@-moz-document url(http://board.gulli.com/), domain(board.gulli.com) {
body
{	margin: -100px 20px 10px 20px !important;
}

/* CP-Leiste nach Oben-Links*/
#navbuttons ul {
	position: fixed !important;
	top: -2px !important;
	left: 0px !important;
	background-color: #333 !important;
	padding: 5px !important;
	width: 100% !important;
	visibility: visible !important;
	text-align: left !important;
}
/* NÃ¼tzliche-Links-Dropdown nach Oben-Rechts*/
#usercptools_menu {
	position: fixed !important;
	top: 20px !important;
	left: 450px !important;
}

/* Thema-durchsuchen-Dropdown nach Oben-Rechts*/
#threadsearch_menu {
	position: fixed !important;
	top: 40px !important;
	width: 184px !important;
}
/* Themen-Optionen-Dropdown nach Oben-Rechts*/
#threadtools_menu {
	position: fixed !important;
	top: 40px !important;
	width: 243px !important;
}
/* Schnellsuche oben links positionieren */
div#sub_search {
	position: fixed !important;
	top: 20px !important;
	left: 0px !important;
	background-color: #333 !important;
	padding: 8px 3px 3px 10px !important;
	color: #000 !important;
	width: 100% !important;
	border-bottom: 1px solid #680600 !important;
	height: 20px !important;

}
/* Themen-Optionen/Forum-Optionen oben rechts positionieren */
td.vbmenu_control#threadtools,
td.vbmenu_control#forumtools
{	position: fixed !important;
	top: 24px !important;
	background-color: #333 !important;
	right: 0px !important;
	width: 175px !important;
}
/* Thema-Durchsuchen/Forum-Durchsuchen oben links positionieren */
td.vbmenu_control#threadsearch,
td.vbmenu_control#forumsearch
{	position: fixed !important;
	top: 24px !important;
	background-color: #333 !important;
	right: 175px !important;
	width: 175px !important;
}
/* Neues Thema und Antworten-Button oben rechts fixieren */
td.tcat[align="right"] {
	position: fixed !important;
	top: 0px !important;
	right: 3px !important;
	width: 106px !important;
	text-align: right !important;
	background-color: #333 !important;
}
/*Signatur hervorheben*/
.signaturealt1, .signaturealt2 {
	border-left: 3px solid #680600 !important;
	border-top: 1px solid #680600 !important;
	background-image: none !important;
	vertical-align: bottom !important;
}
/*Bereich mit unterem Antwortbutton ausblenden*/
td.smallfont[align="right"]
{	display: none !important;
}
/*Graue Linie an der vorherigen Position der CP-Leiste entfernen*/
div.tcatd#navbuttons
{	visibility: hidden !important;
}
/*Seitennavigation oben festmachen*/
.pagenav
{	position: fixed !important;
	top: 2px !important;
	right: 130px !important;
}
/*Private Nachrichten oben festmachen*/
div.urcorner
{	position: fixed !important;
	top: 3px !important;
	right: 350px !important;
	background-color: transparent !important;
	letter-spacing: -1px;
	word-spacing: -1px;
}
}
