css:
@-moz-document domain('facebook.com'){

body{
    background: #d5d5d5 !important;
    font-family: "Segoe UI" !important;
}

#blueBar{
background-color: transparent !important;
}
.slim #blueBar, #blueBarHolder{
border: none !important;
box-shadow: none !important;
}

.slim #blueBar, #blueBarHolder .slim #blueBar, #blueBarHolder {
position: relative !important;
}

#pageHead{
    margin-top: 10px !important;
    background-color: #3B5998;
    padding: 4px 10px;
    margin-bottom: 10px !important;
    border-bottom: 1px rgba(33,33,33,0.3) solid !important;
}
    #q, .uiTypeAhead, .uiSearchInput{
        background-color: #3B5998 !important;
        color: #aaa !important;
    }
    .uiSearchInput button{
    display: none !important;
    }
    
    .slimHeader #navSearch .uiSearchInput {
    border-top: none !important;
    }

#globalContainer{
    background-color: #f0f0f0;
    padding: 10px;
}

#contentCol, #mainContainer{
border: none !important
}

#boulder_fixed_header{
display: none !important;
}

.uiSideNav .item, .uiSideNav .subitem {
border: none !important;
}

#pagelet_ego_pane_w, .egoOrganicColumn, .ego_section, #pagelet_rhc_footer{
display: none;
}


li.uiUnifiedStory{
border: none !important;
}


span.messageBody, h6{
font-size: 10pt !important;
color: #555;
}

.uiUfi .ufiItem {
    background-color: #F0F0F0 !important;
    border: none !important;

}

.uiPhotoThumb{
border: none !important;
}

.uiStreamMessage .actorName, .uiStreamMessage .passiveName {
  font-weight: normal !important;
  font-size: 1.2em;
}
}
