css:
@-moz-document url-prefix('https://allods.mail.ru/forums/') {
html {background: #5E7195 !important;}
.layout_repeater {background: #9D8D70  !important; border: 1px solid; box-shadow: 0px 2px 5px 1px #505050;}
::selection {background-color: #999 !important; color: white !important; text-shadow: none !important;}

/*========= Background none =====================*/

body, .bodywrap, .layout, .below_body, #navtabs li, #navtabs li span, #navtabs li span a, .popupbody li a, .top_shadow, .separator_line, .bbcode_container, .quote_container, .layout .postdetails_top, .layout .bbcode_container div.bbcode_quote, .top_attachments, .breadcrumb .navbit.lastnavbit
{background: none !important;}

/*========= Links and colors ====================*/

#navbar a, .nouser a, .navbar_advanced_search a, .layout .navlinks a, #footer_links a, .forumhead a
{color: #E1C084 !important; text-decoration: none !important; text-shadow: 0 0 2px #000;}

#navbar a:hover, .nouser a:hover, .navbar_advanced_search a:hover, .layout .navlinks a:hover, #footer_links a:hover, .forumhead a:hover
{color: #FFF !important;}

.forumdisplaypopups li h6 a, .popupbody li a, p.rules_link a, #more_rules ul li a, #forumbits .forumtitle a, #stickies .threadbit .threadtitle a.title, #threads .threadbit .threadtitle a.title, #forumbits .forumtitle a, .layout .threadtitle a, .threadlisthead a, .postcontent a
{color: #FFBB3F !important; text-decoration: none !important; text-shadow: 0 0 2px #000;}

.floatcontainer li a:hover, li.lastnavbit span, .forumdisplaypopups li h6 a:hover, #navtabs li.selected span a, .toplinks ul.isuser li a, #remember, .toplinks form input.default-value, .globalsearch input.textbox,  .popupbody li a:hover, p.rules_link a:hover,  #more_rules ul li a:hover, #pagetitle .description, #pagetitle h1, #forumbits .forumtitle a:hover, .layout .threadtitle a:hover, .threadlisthead a:hover
{color: #FFF !important;}

h2.forumtitle a, #forums .forumbit_post .forumlastpost .lastposttitle a, #forums .forumbit_post .forumlastpost .lastpostby a, li.subforum a
{color: #162D56 !important;}

h2.forumtitle a:hover, #forums .forumbit_post .forumlastpost .lastposttitle a:hover, #forums .forumbit_post .forumlastpost .lastpostby a:hover, li.subforum a:hover
{color: #162D56 !important; }

.navbar_username, .navbar_password, .navtabs, .globalsearch input.textbox, #forumbits .forumhead, #forums .forumhead, .popupbody, .layout .threadlisthead, .layout .searchlisthead, #toplinks ul.nouser li a, .layout .navlinks, .wgo_block h2
{background: #4D5769 !important;}

.remember label, #forumbits .forumbit_post .forumstats li, #forumbits .forumbit_post .forumstats_2 li, #forums .forumbit_post .forumstats li, #forums .forumbit_post .forumstats_2 li, span.viewing, p.forumdescription, .lastpostby, .lastpostdate, h4.subforumlistlabel
{color: #4D5769 !important;}

.popupbody
{border-color: #FFBB3F !important;}

.layout_repeater, #forumbits .forumbit_nopost .forumbit_nopost .forumrow, #forumbits .forumbit_post .forumrow, #forums .forumbit_nopost .forumbit_nopost .forumrow, #forums .forumbit_post .forumrow, .layout .threadbit .sticky, .layout .threadbit .nonsticky, .layout .threadbit .deleted
{border-color: #4D5769 !important;}

/*========= Display none ========================*/

.line_shadow, .layout_bottom,#footer_copyright,#footer_morecopyright, .forumhead img.line_img, .forumfoot img, #slider, .signaturecontainer
{display: none !important;}

/*========= Header ==============================*/

#toplinks .loginbutton {background: #4D5769 url('data:image/png;base64,') no-repeat 50% 50%;}
#toplinks .loginbutton:hover {background: #4D5769 url('data:image/png;base64,') no-repeat 50% 50%;}
#toplinks ul.nouser li a {margin: 3px 3px 0 0 !important; padding: 4px 8px !important;}

.remember label {font-size: 11px;}

/*========= Footer =============================*/

.navlinks {margin: 0 auto !important; padding: 8px 0 !important; width: 95%;}

.wgo_block {margin: 15px auto 0 auto !important; width: 95%;}
.wgo_block .bot_info_block {margin: 0 !important; padding: 0 !important;}
.wgo_block h2 {border-bottom: 1px solid #9D8D70 !important; padding: 5px 0 !important;}
.wgo_block div.floatcontainer {top: 0 !important; background: #4D5769 !important;}
.wgo_block .wgo_subblock {border-color: #9D8D70 !important; width: 100% !important; padding: 10px 0 !important;}
.wgo_block .wgo_subblock div, .wgo_block .wgo_subblock ol {padding: 0 32px !important;}

#footer_links {margin: 0 auto; width: 95%;}
#footer_links a {margin: 0 0 0 15px !important;}
#footer_links a, #footer_links a em {background: #4D5769 !important;}
#footer_links a em {height: 25px;}

#footer_time {padding: 5px 0 !important;}
#footer_time, #footer_time .time {color: #E1C084 !important; text-shadow: 0 0 1px #000000;}

/*========= Forums images =============================*/

.subforum img.inlineimg {width: 11px; height: 11px; padding: 0 0 0 11px; box-sizing: border-box;}

.subforum img.inlineimg[src="images/statusicon/subforum_new-48.png"] {
background: url('data:image/png;base64,') left center no-repeat;background-size: contain;}

.subforum img.inlineimg[src="images/statusicon/subforum_old-48.png"] {
background: url('data:image/png;base64,') left center no-repeat;background-size: contain;}

div.foruminfo img {width: 48px; height: 48px; padding: 0 0 0 48px; box-sizing: border-box;}

div.foruminfo img[src="images/statusicon/forum_new-48.png"] {
background: url('data:image/png;base64,') center center no-repeat;background-size: contain;   
}

div.foruminfo img[src="images/statusicon/forum_old-48.png"] {
background: url('data:image/png;base64,') center center no-repeat;background-size: contain;   
}

/*========= Forums =============================*/

.body_wrapper {padding: 25px 8px 20px 8px !important; top: 20px !important;}

.forumhead {-webkit-border-top-left-radius: 0 !important; border: 1px solid #4D5769 !important;}
.layout #forums .forumhead {padding: 0 0 7px 0 !important;}

.forumrow {padding: 5px 0 3px 0 !important;}

.childforum li:last-child {margin: 0 0 20px 0;}
.below_body {height: auto !important; bottom: auto !important;}

.logo {top: -274px !important;}
#header {height: 45px !important;}
#toplinks {margin: 0 !important;}

#toplinks .loginbutton:hover {cursor: pointer;}
.navbar_username input, .navbar_password input, .globalsearch input.textbox {outline: 1px solid #E1C084 !important;}
.navbar_username input:focus, .navbar_password input:focus {outline-offset: -1px !important;}
#navbar {margin: 0 !important; height: 32px !important; background: #795548 !important;}
#navtabs {height: 32px !important; padding: 0 0 0 15px !important;}
#navtabs .selected {padding: 0 !important;}
#navtabs li {margin: 0 !important; top: 8px !important;}
#navtabs li.selected, #navtabs .selected a {height: auto !important;}
#navtabs li span a {padding: 0 15px 0 0 !important;}
.globalsearch {width: 320px; bottom: 0; right: 0;}
.globalsearch, .navbar_search, .navbar_advanced_search {height: 33px;}
.globalsearch .textboxcontainer {padding: 3px 0 0 0 !important;}
.globalsearch .textboxcontainer span {padding: 2px 0 0 0 !important;}
.globalsearch .buttoncontainer span {padding: 6px 0 0 0 !important;}
.globalsearch .searchbutton {left: -25px !important;}
.globalsearch input.textbox {width: 140px;}
.breadcrumb .navbit {background: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8/9hAAAAcklEQVQ4T2NkQAPVyb4L/v9nONA2b/MCdDlsfEZkQZBmID8eJAY0JJEYQ1AMqEryTWBkZJgPM5QYQ1AMAGkk1RAMA0g1BKsBpBiC0wBiDcFrADGG0NYAYmKENoFIjM2wxEbdhESKzVhdQHFmAplKanYGAB7FcBEso8LwAAAAAElFTkSuQmCC') no-repeat 105% 0% !important;}
.navbar_search {bottom: 0; right: 0; width: 220px;float: left;}
.navbar_advanced_search {width: 100px; top: 0; left: 0;float: right;}
.navbar_advanced_search li {text-align: center; padding: 3px 0 0 0;}
#globalsearch .navbar_advanced_search a {font: normal 11px Tahoma;}
#globalsearch .navbar_advanced_search a:hover {font: normal 11px Tahoma;}
#navtabs ul.floatcontainer {top: 24px !important; height: 33px;}
.popupbody {padding: 0px 0 7px 10px !important; border: 1px solid #FFBB3F !important; min-width: 150px !important; min-height: 34px !important;}

.forumdisplaypopups .popupbody {top: 20px !important;}
.forumsearch.popupmenu .popupbody {width: 230px; padding: 5px !important; left: -115px !important; top: 20px !important;}

.forumbit_post .forumstats, .forumbit_post .forumstats_2 {background: linear-gradient(to right, rgba(255,255,255,0) 98.5%,rgb(123, 99, 77) 99.5%,rgb(123, 99, 77) 100%) !important; width: 8% !important;}

.layout .threadlisthead, .layout .searchlisthead {border: 1px solid #4D5769 !important;}
#forumbits .forumhead, #forums .forumhead, .layout .threadlisthead, .layout .searchlisthead {padding: 5px 0 !important;}
#forumbits .forumhead h2, .forumbit_nopost .forumhead h2, .forumbit_post .forumhead h2 {padding: 7px 0 !important;}
.foruminfo  {padding: 0 0 8px 0 !important;}
.layout .threadlisthead span.threadinfo, .layout .threadbit .threadinfo {width: 65% !important;}
.layout .threadlisthead span.threadstats, .layout .threadlisthead span.threadnotification {width: 153px !important;}
.threadlisthead span.threadlastpost {width: 17% !important;}
.threadbit .threadstats li {margin: 0 8px 0 0 !important;}

#forumbits .forumbit_post .forumrow .forumlastpost, #forums .forumbit_post .forumrow .forumlastpost {width: 39% !important;}

.below_threadlist .threadpagenav, .navpopupmenu.popupmenu.nohovermenu { margin-top: 0 !important;}

.left_block_bot, .right_block_bot, .bot_info_block {background: #795548 !important;}
#forum_info_options #posting_rules {width: 478px !important; padding-top: 4px !important;}
#thread_rules {width: 60% !important;}
#more_rules {width: 40% !important;}
#icon_legends dd, #thread_rules, #more_rules li {color: #FFF !important;}
p.rules_link {width: 40% !important; padding-left: 60% !important;}

#forumbits .forumbit_nopost .forumbit_nopost .forumrow, #forumbits .forumbit_post .forumrow, #forums .forumbit_nopost .forumbit_nopost .forumrow, #forums .forumbit_post .forumrow, .layout .threadbit .sticky, .layout .threadbit .nonsticky, .layout .threadbit .deleted {border-left: 1px solid;border-right: 1px solid;border-bottom: 1px solid;background: #CEB27E;}

.layout .postbitlegacy .title.icon {color: #859093 !important;}

.layout .postbitlegacy .postdetails {
background: linear-gradient(to right, rgb(123, 99, 77) 21%,rgb(243, 224, 168) 21%,rgb(243, 224, 168) 100%) !important; border-bottom: 1px solid #4D5769 !important;
}
.layout .postbitlegacy .posthead, .layout .eventbit .eventhead, .layout .postbitdeleted .posthead {
background: #4D5769 !important; margin: 0 !important; border-top: 1px solid #F3E0A8;
}

.layout .bbcode_container div.bbcode_quote, .top_attachments {background: rgba(133,144,147,0.5) !important;}
 
.layout .postbitlegacy .content {color: #000000 !important;}
.layout .postbitlegacy .content font[color="DarkOrange"] {color: #741400 !important;}
.layout .postbitlegacy .content font[color="Red"] {color: #980000 !important;}
.layout .postbitlegacy .content font[color="RoyalBlue"] {color: #73B3F0 !important;}
.bbcode_quote {background-color: #B68248 !important;}

.layout .lastedited, .layout .lastedited a {color: #424242 !important;}
.layout .signature {border-top: 1px solid #424242 !important;}

#threadlist {margin-top: 15px;}

}
