css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.cnbeta.com") {
body{
background:RGB(75,75,75)!important;
}
/*全局链接设置*/
/*链接未点击*/
* a:link{
       color:RGB(78,78,78) !important;
       text-decoration:none!important;  
       font-family: "Microsoft Yahei",sans-serif !important; 
}
/*已点击链接*/
* a:visited
{color:RGB(105,105,105) !important;
 text-decoration:none!important; 
 font-family: "Microsoft Yahei",sans-serif !important; 
}  

/*鼠标放在链接上未点击*/
* a:hover{
color:RGB(0,32,96) !important;
text-shadow:0 2px 2px rgba(0,0,0,.2) !important;
font-family: "Microsoft Yahei",sans-serif !important; 
} 

#h_left #guider,#content #confident,#h_left #logo,#sideBar #votes,#EditorRecommendBox,#essential_feedback{
display:none!important;
}
div#head{
height:70px!important;
}
div.title,div.title .leftBorder,div.title .rightBorder,div.title .ADMenuOff{
background:transparent!important;
color:RGB(228,228,228) !important;
font-size:17px!important;
font-family: "Microsoft Yahei",sans-serif !important; 
padding-top:2px!important;
font-weight:normal!important;
}
div.title span{
height:27px!important;
}
div.title .ADMenuOn{
background:RGB(247,109,56)!important;
color:RGB(254,254,254) !important;
}
div.subTitle{

background:transparent!important;
height:2px!important;
border:none!important;
}
#ADcon0 .newslist{
background:RGB(250,250,250)!important;
border:1px solid RGB(220,220,220)!important;
color:#333333!important;
font-family: "Microsoft Yahei",sans-serif !important; 
}
.newslist dl dt.topic{
font-size:16px!important;
}
div#ADcon0 div.newslist dl dd.desc span{
font-size:12px!important;
font-family: "Microsoft Yahei",sans-serif !important;
}
.newslist dl dd.detail{
border-top:1px solid RGB(220,220,220)!important;
background:transparent!important;
}
.newslist dl dd.detail span{
color:RGB(94,94,94) !important;
}
.newslist dl dd.author span{
border-bottom:1px solid RGB(220,220,220) !important;
}
div#ADcon0 div.newslist dl dd.desc span span{
font-weight:normal!important;
}
}
