css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.torrentz.com/search") {

div.error			{display: block !important; background: #fff !important; margin: auto !important; width: 92% !important; border: 0.7em solid #09f !important; text-align: center !important;}

#suggestbox,
form.profile,
div.top ul li,
div.top ul li a,
div				{display: none !important;}

div.top,
div.results,
form.search div.button,
form.search div.box		{display: block !important; background-image: none !important;}

div.top ul			{float: right !important; margin-top: -5em !important; background-image: none !important;}

div.top h1,
div.top h1 a			{text-align: center !important; margin: auto !important; float: none !important; background-image: none !important;}

div.top h1 a:hover		{color: #0ff !important;}

div.results,
div.results h2,
div.top,
form.search,
form.search fieldset.box	{background-color: #09f !important; background-image: none !important;}

div.results p span span,
div.results p span a,
div.button input		{background-color: #0ff !important; color: #09f !important; background-image: none !important;}

div.results p span span,
div.results p span a		{border: 1px solid #09f !important; background-image: none !important;}

div.results h2			{color: #fff !important; background-image: none !important;}

div.results h2 a img		{float: right !important; margin-right: 1em !important; margin-top: -1em !important; background-image: none !important;}

div.top h1 a			{color: #fff !important; background-image: none !important;}
div.top h1 a:hover		{color: #0ff !important; background-image: none !important;}

input#thesearchbox		{border-right: 0.7em solid #09f !important; width: 83% !important; background-image: none !important;}

}










@-moz-document url("http://www.torrentz.com/") {

div.results			{display: none !important;}

div.top h1 a:hover		{color: #0ff !important;}

}










@-moz-document url-prefix("http://www.torrentz.com/") {

div.top h1 a:hover		{color: #0ff !important;}

div.results h2 a		{color: #0ff !important;}

div.download,
div.trackers,
div.files,
div.results,
div.results h2,
div.top,
form.search,
form.search fieldset.box	{background-color: #09f !important; background-image: none !important;}

div.results p span span,
div.results p span a		{border: 1px solid #09f !important; background-image: none !important;}

div.download h2 span		{color: #fff !important; font-weight: bold !important;}

div.download h2,
div.trackers h2,
div.files h2,
div.results h2			{color: #fff !important; background-image: none !important;}

div.results h2 a img		{float: right !important; margin-right: 1em !important; margin-top: -1em !important; background-image: none !important;}

div.download p,
div.trackers p,
form.profile,
div.top ul li,
div.top ul li a,
div				{display: none !important; background-image: none !important;}

div.trackers			{width: 100% !important;}

div.top,
div.download,
div.trackers,
div.files,
form.search div.button,
form.search div.box		{display: block !important; background-image: none !important;}

div.top ul			{float: right !important; margin-top: -5em !important; background-image: none !important;}

div.top h1,
div.top h1 a			{text-align: center !important; margin: auto !important; float: none !important; background-image: none !important;}

div.top,
form.search,
form.search fieldset.box	{background-color: #09f !important; background-image: none !important;}

div.button input		{background-color: #0ff !important; color: #09f !important; background-image: none !important;}

div.download dl dt a span.u	{color: #09f !important;}

div.download dl dt:hover a span.u:hover,
div.download dl dt:hover a span.u,
div.download dl dt:hover a span.n:hover,
div.download dl dt:hover a span.n,
div.download dl dt:hover a:hover,
div.download dl dt:hover a	{background-color: #def !important; border: none !important;}

div.download dl dt a span.n	{border: none !important;}

div.files ul li.t		{color: #000 !important;}

div.download dl,
div.trackers dl,
div.files ul li ul		{border-color: #def !important;}

}
