css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("volokh.com")
{

  /* hide "Quote" facility on comments */
  span.quote
  { display: none !important; }


  /* hide avatars */
  img.avatar
  { display: none !important; }


  /* hide sidebar */
  #content
  { width: auto !important;
    float: none !important; }
  #sidebar
  { display: none !important; }


  /* deembiggen header */
  #header
  { background: #014101 !important;
    padding: 3px 0 !important;
    height: auto !important;
    text-align: left !important; }
  #header *
  { padding: 0 !important;
    margin-top: 0 !important;
    margin-bottom: 0 !important;
    display: inline !important;
    font-weight: bold !important; }
  .header-title h1 { font-size: inherit !important; }


  /* user font choices */
  body,

  #content ul,
  #content ul ul,
  #content ol,

  #header,
  #header h1,
  #nav,

  .post-entry,
  .archives,
  p.post-meta,
  .commentlist p
  { font: inherit !important;
    text-indent: 0 !important;
    color: black !important; }
  h2,
  h2.pagetitle
  .header-title *,
  span.title *,
  h3
  { font: inherit !important;
    font-weight: bold !important;
    font-size: 120% !important; }
  h3 { font-size: 110% !important; }
  pre,
  code,
  textarea,
  textarea#comment
  { font-size: inherit !important; }


  /* hide "Share/Save" and icons */
  .addtoany_list
  { display: none !important; }
  .mcomments,
  .mcategories,
  .post-meta
  { background-image: none !important;
    padding: 0 !important; }
  .mcategories
  { width: auto !important; }
  .bottom-meta
  { border: solid rgb(238,238,238) !important;
    border-width: 1px 0 0 0 !important;
    padding: 2px 0 0 0 !important; }


  /* hide footer */
  #footer
  { display: none !important; }

  /* related posts */
  #crp_related h3, 
  #crp_related ul
  { margin: 0 !important; 
    list-style: inside !important; }


  /* navigation */
  .navigation > *
  { height: auto !important; }


  /* comments */
  #comments,
  .commentlist
  { margin: 0 !important; }
  .commentlist > li
  { padding: 0.5em 0 !important;
    border-color: rgb(238,238,238) !important; }
  .commentlist
  { border-top: thin dotted #bbb !important;
    margin: 0 !important; }


  /* comment date in the upper corner */
  .commentlist > li
  { position: relative !important; }
  .commentlist > li .commentmetadata
  { position: absolute !important;
    top: 0.5em !important;
    right: 0.5em !important; }
  .commentlist > li .commentmetadata a:not(:hover)
  { color: #bbb !important; }


  /* content style */
  #page
  { min-width: 0 !important; }
  #content
  { margin: 0 !important; }
  #wrapper
  { margin: 0 8px !important; }

  p, blockquote
  { text-align: left !important;
    text-indent: 0 !important; }

  blockquote
  { border: solid #ddd !important;
    border-width: 0 0 0 thick !important;
    margin-left: 1em !important; }


  /* 20100101*/
}
