css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("xmlsoft.org") {

body {
    font-size: 12px;
}

td {
    background-color: white !important;
}

pre.programlisting {
  border: none !important;
  padding: 5px;
  margin-left: 10px;
  margin-right: 10px;
  border-radius: 5px;
  background-color: #d8eec9 !important;
}

a {
    color: #4b6738 !important;
    text-decoration: none !important;
}

a:hover {
    border-bottom: 1px solid #4b6738;
}

p,
pre.programlisting {
    line-height: 18px;
}

li {
    list-style-type: none;
    padding: 10px 0 10px 0;
    border-bottom: 1px dotted #4b6738;
}

}
