css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.tumblr.com") {

* { text-shadow: none !important; 
color: #000000 !important }

#dashboard_index,


#dashboard_transcoding,


#dashboard_inbox,



#dashboard_edit_post,



#dashboard_submissions,



#dashboard_drafts,



#dashboard_post_queue,



#dashboard_likes,



#dashboard_followers,



#dashboard_following,



#dashboard_directory,



#dashboard_lookup_contacts,



#main_doc_page,



#recommended_tumblelogs,



#dashboard_settings,



#dashboard_create_tumblelog,



#dashboard_members,



#main_meetups,



#main_about,



#main_explore,



#dashboard_goodies,



#main_developers,



#main_themes,



#dashboard_manage_themes,



#main_edit_theme,



#main_jobs,



#main_policy,

#main_help,


#tagged_display,


#dashboard_tumblelog_settings {

background: url(http://i45.tinypic.com/2cgd76f.png) 91.5% 100% fixed no-repeat, url(http://i49.tinypic.com/2yumzvm.png) 0.2% 100% fixed no-repeat #ffffff !important;}




#dashboard_index div.l-content,



#dashboard_inbox div.l-content,


#dashboard_transcoding div.l-content,


#dashboard_submissions div.l-content,



#dashboard_drafts div.l-content,



#dashboard_post_queue div.l-content,



#dashboard_likes div.l-content,



#dashboard_followers div.l-content,



#dashboard_members div.l-content,



#tagged_display div.l-content,



#main_tag div.l-content,



#dashboard_tumblelog_settings #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



background:rgba(153, 134, 117, 0.0) !important }



#left_column #posts .notification .block {

  color: transparent !important;

}

#left_column #posts .notification.alt {

  background-color:rgba(51,51,51, 0.6) !important;

}

#left_column #posts .notification.last_notification,

#left_column #posts .notification.single_notification {

  border-bottom-color: transparent !important;

}

#left_column #posts .notification a {

  color:#f7c22d !important;

  border-bottom:0px !important;

}

#left_column #posts .notification blockquote {

  border-left-color:transparent !important;

}

#left_column #posts .notification .avatar {

  border-color: transparent !important;

  background-color:transparent !important;

  opacity:1 !important;

}

#left_column #posts .notification .avatar:hover {

  opacity:1 !important;

}


.selection_nipple { border-bottom-color: rgba(0,0,0,0.1) !important }





#tracked_tags a {color: rgba(0,0,0,1) !important}






.permalink { background: none !important }



#inbox_button {background-color: rgba(0,0,0,0.1) !important;
border:0px solid;

	border-bottom-left-radius:2em !important;
	border:0px solid;

	border-bottom-right-radius:2em !important;
	border-top-right-radius:2em !important; 
	border-top-left-radius:2em !important; }


#preferences_button {background-color: rgba(0,0,0,0.1) !important;
border:0px solid;

	border-bottom-left-radius:2em !important;
	border:0px solid;

	border-bottom-right-radius:2em !important;
	border-top-right-radius:2em !important; 
	border-top-left-radius:2em !important; }


#help_button {background-color: rgba(0,0,0,0.1) !important;
border:0px solid;

	border-bottom-left-radius:2em !important;
	border:0px solid;

	border-bottom-right-radius:2em !important;
	border-top-right-radius:2em !important; 
	border-top-left-radius:2em !important; }

#all_blogs_menu_arrow {background-color: rgba(0,0,0,0.1) !important;
border:0px solid;

	border-bottom-left-radius:2em !important;
	border:0px solid;

	border-bottom-right-radius:2em !important;
	border-top-right-radius:2em !important; 
	border-top-left-radius:2em !important; }

#logout_button {background-color: rgba(0,0,0,0.1) !important;
border:0px solid;

	border-bottom-left-radius:2em !important;
	border:0px solid;

	border-bottom-right-radius:2em !important;
	border-top-right-radius:2em !important; 
	border-top-left-radius:2em !important;
    position: relative !important;
    left: 6px !important;}	

#missinge_button {background-color: rgba(0,0,0,0.1) !important;
border:0px solid;

	border-bottom-left-radius:4em !important;
	border:0px solid;

	border-bottom-right-radius:4em !important;
	border-top-right-radius:4em !important; 
	border-top-left-radius:4em !important; }

#home_button {background-color: rgba(0,0,0,0.1) !important;
border:0px solid;

	border-bottom-left-radius:4em !important;
	border:0px solid;

	border-bottom-right-radius:4em !important;
	border-top-right-radius:4em !important; 
	border-top-left-radius:4em !important; 
	position: relative !important;
	right: 6px !important;}

.avatar_frame {



background-color: rgba(255,255,255,0.1) !important; border-color: rgba(255,255,255,0.1) !important }







.notification {



background: rgba(0,0,0,0.0) !important;



border: rgba(255,255,255,0.1) !important;



background-image: none !important;



margin-bottom: 1px !important }







.notification a { border-bottom: 1px solid rgba(0,0,0,0.1) !important }



.notification a:hover { border-bottom: 1px solid rgba(0,0,0,0.3) !important }







.notification_type_icon { display: none !important }






.nipple { border-right-color: rgba(0,0,0,0.07) !important }




#recommended_tumblelogs {

background:rgba(255, 255, 255, 0.0) !important}






.with_subtitle small { color: rgba(255,255,255,1) !important }



.recessed a { color: rgba(255,255,255,0.7) !important }




h1, .title { color: rgba(0,0,0,0.6) !important }




.no_posts_found, h1.dashboard_header { color: rgba(0,0,0,1) !important }







.dashboard_options_form {



background: rgba(0,0,0,0.1) !important;



border: none !important;



color: rgba(255,255,255,0.7) !important }







.dashboard_options_form_disclaimer, .dashboard_options_form_disclaimer a { color: rgba(255,255,255,0.9) !important }







.blue_note, .blue_note a {



background: rgba(0,0,0,0.2) !important;



color: rgba(0,0,0,0.6) !important }






div#right_column,
div#right_column:after {
  background: none !important;
}








#footer, #footer a {



border: none !important;



color: rgba(0,0,0,1) !important }


#right_column .controls_section *{
background-color: rgba(255,255,255,0.3) !important;
color:#000000 !important}


#tracked_tags *{
background-color: rgba(255,255,255,0.3) !important;
color:#000000 !important;
border: none !important}



.new_post_label{






padding-top:80px !important;

text-transform:lowercase !important;



letter-spacing:2px !important;



text-shadow: none !important;



font-weight:normal !important;



margin:0px !important;

color:#000000 !important



}



.new_post_label:after{



content:"" !important



}















.new_post_label:hover{



color:#eeba2f !important



}



.arrow{display:none !important}



.sub_avatar{width:32px !important; height:32px !important; background-color:transparent !important}



.new_post_label_icon{background-image:none !important}



#new_post{



background: url('http://i47.tinypic.com/904wpd.png') 0% 100% no-repeat;



background-color:#fff !important;



}



img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{



    width:0;



    height:70px;



    padding-right:250px;



    opacity: 0;



}















#pagination a {



background: rgba(0,0,0,0.4) !important;



color: rgba(255,255,255,1) !important }



#pagination a:hover {



color: rgba(0,0,0,1) !important }







#dashboard_controls a, #dashboard_controls .control {



color: rgba(0,0,0,1) !important }







.dashboard_controls_arrow_first_page, .dashboard_controls_arrow_left, .dashboard_controls_arrow_right {



display: none !important }

.dashboard_controls_arrow_first_page, .dashboard_controls_arrow_left, .dashboard_controls_arrow_right {



display: none !important }



#logo {height: 0 !important;







	width: 0 !important;







	padding-left: 300px !important;







	padding-top: 55px !important;
	position: relative !important;
	right: 55px !important;
	top: -2px !important;







	background: url(http://i46.tinypic.com/2clfg2.png) no-repeat !important;}
	}
