@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("94.75.224.58"), domain(darksiderg.com), domain("85.17.184.130")
{


/*In Post Headings*/
td.darkrow1
{
  background-color:#333333!important;
  color:orange!important;
  font-family:Calibri,Arial !important;
  font-size:14px !important;
  border-color:black !important;
}


/*No square tiny pictures*/
DIV > DIV > DIV > DIV > IMG
{
  display:none;
}


/*Forum Description Text - small font and black*/
span.forumdesc
{
  background-color:#FFFFFF !important;
  color:#000000 !important;
  font-family:Calibri,Arial !important;
  font-size:10px !important;
  border-color:black !important;
}


/*Table Headings - Orange Text*/
th
{
  background-color:#FFFFFF!important;
  color:orange!important;
  font-family:Calibri,Arial !important;
  font-size:16px !important;
  border-color:black !important;
}




/*Table Background to White*/
table,td,tr,p,html,center
{
  background-color:#FFFFFF !important;
  color:#000000 !important;
  font-family:Calibri,Arial !important;
  font-size:14px !important;
  border-color:black !important;
}




 

/*Calibri and White Background*/
body
{
  background-color:#000000!important;
  color:#FFFFFF!important;
  font-family:Calibri,Arial !important;
  font-size:12px !important;
}


/*Background*/
div#ipbwrapper3,.row1
{
  background-color:#FFFFFF !important;
  color:#000000 !important;
  font-family:Calibri,Arial !important;
  font-size:14px !important;
  border-color:black !important;
}



/*All DIVs and spans*/
div,span
{
  background-color:#FFFFFF !important;
  color:#000000 !important;
  font-family:Calibri,Arial !important;
  font-size:14px !important;
  border-color:black !important;
}

div.formsubtitle
{
  background-color:#FFFFFF!important;
  color:#FFFFFF!important;
  font-family:Calibri,Arial !important;
  font-size:16px !important;
  border-color:black !important;
}



/*Beginning of code - Page Header Logo REMOVED code*/
div#logostrip,element.style,HTML > BODY > DIV > DIV > A > DIV
{
  /*
    background-image:url("http://img.photobucket.com/albums/v416/beanz1/new-1.gifuck
    background:url("http://img.photobucket.com/albums/v416/beanz1/new-1.gifuck
  */

  display:none;

}
/*End of last item that REMOVES the page header*/



}
