css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url("http://joncom.be/experiments/markdown-editor/edit/") {
/* ========================================================================== */

#output {
	color: #333 !important;
	line-height: 1.6 !important;
	font-size: 13px !important;
	}

#output h1 {
	font-size: 200% !important;
	}
#output h2 {
	padding: .5em 1em !important;
	color: #fff !important;
	background-color: #333 !important;
	font-size: 140% !important;
	}
#output h3 {
	margin: 1em 0 0 !important;
	padding-bottom: .3em !important;;
	border-bottom: 1px solid !important;;
	font-size: 140% !important;
	}
#output h4 {
	padding: .3em .8em;
	border-left: 10px solid;
	font-size: 130% !important;
	}
#output h5 {
	padding: .3em 0;
	border-bottom: 1px dotted !important;
	font-size: 120% !important;
	}
#output h6 {
	font-size: 108% !important;
	}

#output p {
	margin: 1.8em !important;
	}

#output pre {
	padding: 1em 1.6em !important;
	border: 1px solid #ddd !important;
	color: #666;
	background-color: #edefef !important;
	}

#output p > code,
#output li > code {
	margin: auto .3em !important;
	padding: .1em .4em !important;
	border: 1px solid #ddd !important;
	color: #444;
	background-color: #efe6e6 !important;
	border-radius: 3px;
	}

#output blockquote {
	position: relative;
	margin: 1.8em !important;
	padding: 30px !important;
	border: 1px solid #ddd !important;
	color: #888;
	background-color: #f8f8f8 !important;
	}
#output blockquote:before {
	content: "“";
	color: #ccc;
	font-size: 80px;
	font-weight: bold;
	font-family: Arial, Helvetica, sans-serif;
	text-align: left;
	line-height: 40px;
	width: 30px;
	height: 30px;
	position: absolute;
	top: 30px;
	left: 10px;
	}
#output blockquote:after {
	content: "”";
	color: #ccc;
	font-size: 80px;;
	font-weight: bold;
	font-family: Arial, Helvetica, sans-serif;
	text-align: left;
	line-height: 60px;
	width: 30px;
	height: 30px;
	position: absolute;
	bottom: 20px;
	right: 20px;
	}

#output li {
	margin: .4em !important;
	}
#output li li:last-child {
	margin-bottom: 1.6em !important;
	}

/* ========================================================================== */
}
