css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("about:newtab") {

#newtab-scrollbox{
background:#333 !important;
}
.newtab-cell{
background:#444 !important;
border:none !Important;
box-shadow:none !important;
}
.newtab-site{
box-shadow:none !important;
}
.newtab-site:hover::before{
width:100%;
position:absolute;
background:rgba(20,20,20,0.6) !important;
height:30px;
z-index:1000;
content:"";
}

.newtab-control{
-moz-transition-duration:0s !important;
z-index:10000;
height:30px !important;
width:30px !important;
top:0 !important;
margin:0 !important;
padding:0 !important;
}
.newtab-control-pin{
background:url("data:image/png;base64,") !important;
left:0 !important;
}
.newtab-control-pin[pinned="true"]{
background:url("data:image/png;base64,") !important;
}
.newtab-control-block{
background:url("data:image/png;base64,") !important;
right:0 !important;
}
.newtab-title{
font-size:14px !important;
height:30px !important;
line-height:30px !important;
background:rgba(30,30,30,0.85) !important;
color:#DDD !important;
}
#newtab-grid > .newtab-row > .newtab-cell .newtab-thumbnail{
background-size:auto auto !Important;
}

.newtab-grid > newtab-row:nth-child(1) > newtab-cell:nth-child(1) newtab-thumbnail{background-image:none !important;}
.newtab-grid > newtab-row:nth-child(1) > newtab-cell:nth-child(2) newtab-thumbnail{background-image:none !important;}
.newtab-grid > newtab-row:nth-child(1) > newtab-cell:nth-child(3) newtab-thumbnail{background-image:none !important;}
.newtab-grid > newtab-row:nth-child(2) > newtab-cell:nth-child(1) newtab-thumbnail{background-image:none !important;}
.newtab-grid > newtab-row:nth-child(2) > newtab-cell:nth-child(2) newtab-thumbnail{background-image:none !important;}
.newtab-grid > newtab-row:nth-child(2) > newtab-cell:nth-child(3) newtab-thumbnail{background-image:none !important;}
.newtab-grid > newtab-row:nth-child(3) > newtab-cell:nth-child(1) newtab-thumbnail{background-image:none !important;}
.newtab-grid > newtab-row:nth-child(3) > newtab-cell:nth-child(2) newtab-thumbnail{background-image:none !important;}
.newtab-grid > newtab-row:nth-child(3) > newtab-cell:nth-child(3) newtab-thumbnail{background-image:none !important;}


}
