css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url('https://www.tumblr.com/login'),
 url('http://www.tumblr.com/dashboard'),
url('http://www.tumblr.com/new/blog'),
 url('http://www.tumblr.com/inbox'),
url('http://www.tumblr.com/help'),
url('https://www.tumblr.com/preferences'),
url('http://www.tumblr.com/lookup'),
url('http://www.tumblr.com/explore'),
 url('http://www.tumblr.com/following'),
url('http://www.tumblr.com/likes'),
url-prefix('http://www.tumblr.com/new/'),
url-prefix('http://www.tumblr.com/tumblelog/'),
url-prefix('http://www.tumblr.com/tagged/'),
url-prefix('http://www.tumblr.com/spotlight/'),
 url-prefix('http://www.tumblr.com/edit/') {

/*sets text color for ALL text*/

@font-face {

     font-family: 'PermanentMarkerRegular';

    src: url('http://static.tumblr.com/blpgwiz/uJKm3yca4/permanentmarker-webfont.eot);

    src: url('http://static.tumblr.com/blpgwiz/uJKm3yca4/permanentmarker-webfont.eot') format('embedded-opentype'),

         url('http://static.tumblr.com/blpgwiz/gIwm3yccn/permanentmarker-webfont.woff') format('woff'),

         url('http://static.tumblr.com/blpgwiz/foAm3ycd6/permanentmarker-webfont.ttf') format('truetype'),

         url('http://static.tumblr.com/blpgwiz/GqVm3ycdr/permanentmarker-webfont.svg') format('svg');

    font-weight: normal;

    font-style: normal;



}



a, a:visited{
	font-family: 'PermanentMarkerRegular';
}





{ color: #000 !important;}

#left_column #posts .notification .avatar {

          
                        

			opacity:0.6 !important;

}






/*BIG BACKGROUND begin - background for the entire screen*/

body {
  background: url('http://static.tumblr.com/blpgwiz/w6zm55mu0/one_direction.jpg') center #fff no-repeat fixed !important;
 background-size: 100% 100%;
background-color: #fcf !important;
color:#000000!important;
 font-family: 'PermanentMarkerRegular';

}
/*BIG BACKGROUND end*/

h1,h2,h3,h4,h5,h6{color:#000000 !important}

a, a:visited{color:#000000 !important}




/*CONTENT begin - background for posts, icon bar, sidebar*/


#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {


  background: #fff !important;
  opacity: 0.75 !important;
	-moz-box-shadow: 10px 10px 5px #888;
-webkit-box-shadow: 10px 10px 5px #888;
box-shadow: 10px 10px 5px #888;
  

}


/*CONTENT*/ 



/*POST BACKGROUND begin*/

#posts LI[class*=" post"] {
background: #fff !important;
-moz-box-shadow: 10px 10px 5px #888;
-webkit-box-shadow: 10px 10px 5px #888;
box-shadow: 10px 10px 5px #888;
border: 2px #fff !important;



}


/*POST BACKGROUND end*/

/*RADAR begin - removes radar blue frame - softens appearance*/

.post.photo_post .photo, .post.photo_post .video_thumbnail {

	-moz-box-shadow: 10px 10px 5px #888;
-webkit-box-shadow: 10px 10px 5px #888;
box-shadow: 10px 10px 5px #888;
border: 2px #fff !important;

}




.content.is_media, .mask.top, .mask.bottom {

  background: none !important;

}

/* avatars*/

#left_column #posts .post .post_avatar 

	{-moz-border-radius: 38px !important;  

	-webkit-border-radius: 60px !important;  

	width: 64px !important; height: 64px !important;  

	margin: 0px !important;} 

#left_column #posts .post .sub_avatar_inner 

	{-moz-border-radius: 38px !important;  

	-webkit-border-radius: 60px !important;  

	width: 32px !important; height: 32px !important;  

	margin: 0px !important;} 

/*RADAR end*/


/*NEW CONTENT begin - text post message body background*/


.controls_section{
	border:3px solid #fff  !important;
	-moz-box-shadow: 10px 10px 5px #888;
-webkit-box-shadow: 10px 10px 5px #888;
box-shadow: 10px 10px 5px #888;
}

#logo {height: 0 !important;
width: 0 !important;
padding-left: 240px !important;
padding-top: 70px !important;
background: url(http://static.tumblr.com/blpgwiz/fqPm55mwz/1d_logo.png) no-repeat !important;
}




.new_post_label_icon{background-image:none !important}



#new_post{
background: url('http://static.tumblr.com/blpgwiz/7HDm55n0d/id_icons.png') center no-repeat;
background-color:#fff !important;
}


img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
    width:0;
    height:70px;
    padding-right:250px;
    opacity: 0;
}

.mceContentBody {
color: #000; !important;
background: #fff !important;
-moz-box-shadow: 10px 10px 5px #888;
-webkit-box-shadow: 10px 10px 5px #888;
box-shadow: 10px 10px 5px #888;
border: 2px #fff !important;
}

/*NEW CONTENT end*/

/*DO NOT DELETE THIS BRACKET*/}
