css:
@-moz-document url-prefix("http://www.feedly.com/home") {

div#feedlyPage { width:646px !important; }
div#mainArea { width:646px !important; }
div#sideArea { display:block !important; position:fixed !important; margin:50px 0px 0px 680px !important; width:250px !important; }
div#fusionModule_part { display:none !important; }
div#discoverModule_part { display:none !important; }

div#floatingBar { display:none !important; }
div.pageActionBar { margin:500px 100px 0px 900px !important; position:fixed !important; width:50px !important; }
img.pageAction { margin-bottom:20px !important; }
img.floatingPageActionMarkAsRead { display:none !important; }
div#store { height:300px !important; }
div#sponsorsModule_part { display:none !important; }

div.label h2 { margin-top:40px !important }

div.u4entry { overflow:hidden; }

div.topRecommendedEntry { float:none !important; margin:0px !important; width:100% !important; }
div.visual div { display:none !important; }
div.placeholder { display:none !important; }
span#pageActionLayouts { display:none !important; }
div#aboutArea { display:none !important; }
div#feedlyPart0 { min-height:800px !important; margin-top: 50px !important; }

.pageAction { opacity: 1 !important; }
.wide #feedlyFrame { margin:0px !important; }
.unread { color:#4777b5 !important; }

input#searchBox { display:none !important; }

}
