css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitch.tv") {

body {
    color: ffffff !important;
}

.primary_button {
    background: -moz-linear-gradient(center top , rgba(255, 255, 255, 0.2), rgba(0, 0, 0, 0.2)) repeat scroll 0 0 #222 !important;
    border-color: #000 !important;
    box-shadow: 0 1px 0 rgba(255, 255, 255, 0.1), 0 1px 0 rgba(255, 255, 255, 0) inset, 0 0 1px rgba(255, 255, 255, 0.5) inset !important;
    color: #FFFFFF !important;
    text-shadow: 0 1px 0 #000000 !important;
    background-color: #222 !important;
}

.title a {
    color: #222 !important;
}

.tab a {
    color: #222 !important;
}

.game_filter a {
    color: #222 !important;
}

.page_links a{
    color: #222 !important;
}

.chat_line a {
    color: #222 !important;
    text-shadow: 0 1px 0 #444 !important;
}

.normal_button {
    color: #222 !important;
}

#metagame a:link {
    color: #222 !important;
    text-shadow: 0 1px 0 #444 !important;
}

#metagame a:visited {
    color: #222 !important;
    text-shadow: 0 1px 0 #444 !important;
}

#metagame a:hover {
    color: #222 !important;
    text-shadow: 0 1px 0 #444 !important;
}

#metagame a:active {
    color: #222 !important;
    text-shadow: 0 1px 0 #444 !important;
}

input.text:focus,select:focus,textarea:focus {
    outline:0;background-color:#fff !important;
    box-shadow:0 0 3px 1px rgba(2,2,2,0.75) !important;
    -moz-box-shadow:0 0 3px 1px rgba(2,2,2,0.75) !important;
    -webkit-box-shadow:0 0 3px 1px rgba(2,2,2,0.75) !important;
    border:1px solid rgba(2,2,2,0.75) !important;
}

.dropmenu a, .ui-menu a, .ui-multiselect-menu a {
    color: #aaa !important;
}

.dark_wrapper .darken a, .dark_wrapper .dropmenu a, .dark_wrapper .ui-menu a, .dark_wrapper .ui-multiselect-menu a {
color: #888 !important;
}

.dropmenu.menu-like .dropmenu_action, .menu-like.ui-menu .dropmenu_action, .menu-like.ui-multiselect-menu .dropmenu_action, .dropmenu.menu-like .language, .menu-like.ui-menu .language, .menu-like.ui-multiselect-menu .language, .dropmenu.menu-like .ui-menu-item a, .menu-like.ui-menu .ui-menu-item a, .menu-like.ui-multiselect-menu .ui-menu-item a, .ui-menu .dropmenu_action, .ui-menu.ui-multiselect-menu .dropmenu_action, .ui-menu .language, .ui-menu.ui-multiselect-menu .language, .ui-menu .ui-menu-item a, .ui-menu.ui-multiselect-menu .ui-menu-item a {
    color: #aaa !important;
}

#menu_defaults .game_filter.selected a, #menu_filtered .game_filter.selected a, #menu_featured .game_filter.selected a {
 border: 1px solid #2E2A35 !important;
 background-color: #222 !important;
 color: #888 !important;
}

#site_footer a {
    color: #FFFFFF !important;
    font-weight: 500 !important;
    text-shadow: 0 1px 0 #000000 !important;
}

}
