@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("gallery-dump.com") {





/*Ben's Hovered HREF Links Color Changing Code:*/
a:hover
{
  color:#FF0000 !important;
}

/*Ben's Hovered HREF Links Color Changing Code:*/
a:visited
{

  color:#ff9933 !important;
}


html,table,tr,p,body,div,a
{
  background-color:#FFFFFF!important;
  background:#FFFFFF!important;
  color:#000000!important;
  font-family:Calibri,Arial !important;
  font-size:14px;
  border-color:black !important; 
}




/*Comment of how many links are listed on items*/
span.typ
{
  color:#aaaaaa;
  background-color:#FFFFFF;
}




/*Page Title Heading*/
DIV > DIV > DIV > DIV > H1 > A
{
  background-color:#FFFFFF!important;
  color:#ff9933!important;
  font-family:Calibri,Arial !important;
  font-size:20px;
  border-color:black !important; 
}



td,b,p
{
  color:#000000!important;
}



/*Sidebar Links*/
a.ml
{
  color:#FFFFFF;
  background-color:#000000;
}





FONT
{
  color:#000000;
  background-color:#FFFFFF;

}


/*Lists*/
ul,li
{
  background-color:#FFFFFF!important;
  color:#000000!important;
  font-family:Calibri,Arial !important;
  border-color:black !important; 
}

/*Click HERE links*/
HTML > BODY > DIV > DIV > DIV > DIV > DIV > DIV > CENTER > A
{
  color:orange !important;
  font-weight:bold !important;
  text-decoration: underline !important;
}

/*Dates:*/
HTML > BODY > DIV > DIV > DIV > DIV > DIV > DIV > DIV > DIV > DIV > DIV > B
{
  font-size:18px !important;
}

}
